CREATE TABLE group_membership (oid BIGINT GENERATED ALWAYS AS IDENTITY NOT NULL, groupId VARCHAR(255), userId VARCHAR(255), roleId VARCHAR(255), PRIMARY KEY (oid))
CREATE TABLE site (ID VARCHAR(255) NOT NULL, NAME VARCHAR(255), REF VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE user_eid (oid BIGINT GENERATED ALWAYS AS IDENTITY NOT NULL, eid VARCHAR(255), uuid VARCHAR(255), PRIMARY KEY (oid))
CREATE TABLE friends (oid BIGINT GENERATED ALWAYS AS IDENTITY NOT NULL, uuid VARCHAR(255), friendUuid VARCHAR(255), firstName VARCHAR(255), lastName VARCHAR(255), friendStatus VARCHAR(255), lastUpdate BIGINT, PRIMARY KEY (oid))
CREATE TABLE subject_permission (oid BIGINT GENERATED ALWAYS AS IDENTITY NOT NULL, subjectToken VARCHAR(255), groupid VARCHAR(255), permissionToken VARCHAR(255), roleid VARCHAR(255), PRIMARY KEY (oid))
CREATE TABLE aclIndex (STATEMENT_KEY VARCHAR(255) NOT NULL, GRANTED SMALLINT DEFAULT 0, PERMISSIONTOKEN VARCHAR(255), SUBJECTTYPE VARCHAR(255), SUBJECTTOKEN VARCHAR(255), REF VARCHAR(255), PRIMARY KEY (STATEMENT_KEY))
