define(['jquery', 'oae.core'], function($, oae) {

    return function(uid, showSettings, widgetData) {
        // The user management container
        var $rootel = $('#' + uid);

        // Variable that will be used to keep track of the current infinite scroll instance
        var infinityScroll = false;

        /**
         * Render the available configuration modules and their configured values
         */
        var renderUserManagement = function() {
            oae.api.util.template().render($('#usermanagement-template', $rootel), null, $('#usermanagement-container', $rootel));
        };

        /**
         * TODO
         */
        var getUserList = function() {
            // Disable the previous infinite scroll
            if (infinityScroll) {
                infinityScroll.kill();
            }

            // Detect whether or not we need to do a search by checking if
            // the History.js state has a query parameter
            var query = History.getState().data.query;
            $('#usermanagement-search-query', $rootel).val(query);

            // Set up the list actions
            var initialContent = null;
            if (!query) {
                initialContent = oae.api.util.template().render($('#usermanagement-list-actions-template', $rootel), {
                    'context': widgetData.context
                });
            }

            // Set up the infinite scroll
            infinityScroll = $('.oae-list', $rootel).infiniteScroll('/api/search/general', {
                'limit': 12,
                'scope': widgetData.context.alias,
                'q': query,
                'resourceTypes': 'user',
                'tenantAlias': widgetData.context.alias
            }, '#usermanagement-list-template', {
                'initialContent': initialContent,
                'postProcessor': function(data) {
                    // Let the template know whether or not the current list
                    // is a main list or a search list, as different paging
                    // keys need to be provided for each
                    data.query = query;
                    return data;
                },
                'emptyListProcessor': function() {
                    oae.api.util.template().render($('#usermanagement-noresults-template', $rootel), {
                        'query': query
                    }, $('.oae-list', $rootel));
                }
            });
        };

        /**
         * Initiate a search with the search query entered by the user. An empty
         * search will just show the normal list.
         */
        var search = function() {
            var query = $('#usermanagement-search-query', $(this)).val();
            // Push the new query to a new History.js state. We make sure to take the
            // existing state data parameters with us and construct a new URL based on
            // the existing base URL, allowing for page refreshing and bookmarking.
            var newState = $.extend({}, History.getState().data, {
                'query': query
            });

            // We cannot rely on the "current" url as that can be different depending on the browser.
            // Most browsers will display `/me/library`, IE9 will be display `/me#library` however.
            // The cleanUrl in the History.js state will always be `/me/library`.
            var url = $.url(History.getState().cleanUrl).attr('path') + '?q=' + oae.api.util.security().encodeForURL(query);
            History.pushState(newState, $('title').text(), url);

            // Avoid that the search form is being submitted
            return false;
        };

        /**
         * Add the different event bindings
         */
        var addBinding = function() {
            // Set up search event
            $rootel.on('submit', '#usermanagement-search-form', search);

            // Listen to History.js state changes and listen to the event that indicates
            // that a piece of content has been deleted, so the library can be reloaded
            $(window).on('statechange', function() {
                // Only re-load the list when the widget is currently visible
                if ($rootel.is(':visible')) {
                    getUserList();
                }
            });

            $(document).on('oae.createuser.done', function() {
                // Only re-load the list when the widget is currently visible
                if ($rootel.is(':visible')) {
                    getUserList();
                }
            });

            // Trigger the create user dialog and pass the context data to it
            $rootel.on('click', '#usermanagement-createuser', function() {
                $(document).trigger('oae.trigger.createuser', widgetData);
            });

            $rootel.on('click', '.oae-list-container > ul > li:not(.oae-list-actions)', function() {
                var userId = $(this).find('[data-id]').attr('data-id');
                $(document).trigger('oae.trigger.manageuser', {
                    'userId': userId,
                    'context': widgetData.context
                });
            });
        };

        /**
         * Initialize the user management related functionality
         */
        var initusermanagement = function() {
            addBinding();
            renderUserManagement();
            getUserList();
        };

        initusermanagement();

    };
});
