define(['jquery', 'oae.core'], function($, oae) {

    return function(uid, showSettings, widgetData) {

        // The widget container
        var $rootel = $('#' + uid);

        // Cache the group profile
        var groupProfile = null;

        /**
         * Render the about group widget
         */
        var renderAboutGroup = function() {
            oae.api.util.template().render($('#aboutgroup-template', $rootel), {
                'groupProfile': groupProfile
            }, $('.modal-body', $rootel));
        };

        /**
         * Initialize the aboutgroup widget
         */
        var initAboutGroup = function() {
            $(document).on('click', '.group-trigger-aboutgroup', function() {
                // Show the modal
                $('#aboutgroup-modal', $rootel).modal();
                $(document).trigger('oae.context.get', 'aboutgroup');
            });

            $(document).on('oae.context.send.aboutgroup', function(ev, data) {
                groupProfile = data;
                renderAboutGroup();
            });
        };

        initAboutGroup();

    };
});
