define(['jquery', 'oae.core'], function($, oae) {

    return function(uid, showSettings) {

        // The widget container
        $rootel = $('#' + uid);

        // Keep track of the user unsubscribing from emails
        var unsubscribedFromMail = oae.data.me.emailPreference === 'never' ? true : false;

        /**
         * Resets the widget to its original state when the modal is closed
         */
        var reset = function() {
            // Reset all the forms
            $('form', $rootel).each(function(i, form) {
                // Reset the form
                $(form)[0].reset();
                // Clear the validation messages from the form
                oae.api.util.validation().clear(form);
            });

            // Reset the `unsubscribedFromMail` variable to the current email preference
            unsubscribedFromMail = oae.data.me.emailPreference === 'never' ? true : false;

            // Reset the widget to the main view
            showMainView();

            // Show the first tab
            $('#accountpreferences-tab-container ul li:first-child a').click();
        };

        /**
         * Changes the password of the currently authenticated user
         *
         * @return {Boolean}       Returns false to avoid native form submission
         */
        var changePassword = function() {
            var oldPassword = $('#accountpreferences-current-password', $rootel).val();
            var newPassword = $('#accountpreferences-new-password', $rootel).val();

            oae.api.authentication.changePassword(oldPassword, newPassword, function(err) {
                if (err) {
                    if (err.code === 400) {
                        // The user has a non-local account
                        oae.api.util.notification(
                            oae.api.i18n.translate('__MSG__PASSWORD_NOT_UPDATED__'),
                            oae.api.i18n.translate('__MSG__YOUR_PASSWORD_CANNOT_BE_CHANGED_HERE__', 'accountpreferences'),
                            'error'
                        );
                    } else if (err.code === 401) {
                        // Show a 'wrong current password' failure
                        oae.api.util.notification(
                            oae.api.i18n.translate('__MSG__PASSWORD_NOT_UPDATED__'),
                            oae.api.i18n.translate('__MSG__THE_PROVIDED_PASSWORD_IS_INCORRECT__', 'accountpreferences'),
                            'error'
                        );
                    } else {
                        // Show a generic failure notification
                        oae.api.util.notification(
                            oae.api.i18n.translate('__MSG__PASSWORD_NOT_UPDATED__'),
                            oae.api.i18n.translate('__MSG__PASSWORD_UPDATE_FAILED__', 'accountpreferences'),
                            'error'
                        );
                    }
                } else {
                    // Hide the modal after saving
                    $('#accountpreferences-modal', $rootel).modal('hide');

                    // Show a success notification
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__PASSWORD_UPDATED__', 'accountpreferences'),
                        oae.api.i18n.translate('__MSG__PASSWORD_SUCCESSFULLY_UPDATED__', 'accountpreferences')
                    );
                }
            });

            return false;
        };

        /**
         * Update the email and locale preferences
         */
        var updatePreferences = function() {
            var profile = {
                'locale': $('#accountpreferences-language', $rootel).val(),
                'emailPreference': unsubscribedFromMail === true ? 'never' : $('.oae-large-options-container input[type="radio"]:checked', $rootel).val()
            };

            oae.api.user.updateUser(profile, function(err) {
                if (err) {
                    // Show a failure notification
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__PREFERENCES_NOT_UPDATED__', 'accountpreferences'),
                        oae.api.i18n.translate('__MSG__PREFERENCES_UPDATE_FAILED__', 'accountpreferences'),
                        'error'
                    );
                } else {
                    // Hide the modal after saving
                    $('#accountpreferences-modal', $rootel).modal('hide');

                    // Show a success notification
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__PREFERENCES_UPDATED__', 'accountpreferences'),
                        oae.api.i18n.translate('__MSG__PREFERENCES_SUCCESSFULLY_UPDATED__', 'accountpreferences')
                    );

                    // Cache the email preference
                    oae.data.me.emailPreference = profile.emailPreference;

                    // Reload the page if the language has been changed
                    if (profile.locale !== oae.data.me.locale) {
                        setTimeout(function() {
                            document.location.reload();
                        }, 2000);
                    }
                }
            });

            // Return false to avoid default form submit behavior
            return false;
        };

        /**
         * Set up validation for every account preferences form. This will validate and submit the forms or
         * show an error message when appropriate.
         */
        setUpValidation = function() {
            // Validate the password form
            oae.api.util.validation().validate($('#accountpreferences-password', $rootel), {
                'rules': {
                    'accountpreferences-new-password': {
                        'minlength': 6
                    },
                    'accountpreferences-retype-password': {
                        'equalTo': '#accountpreferences-new-password'
                    }
                },
                'messages': {
                    'accountpreferences-new-password': {
                        'required': oae.api.i18n.translate('__MSG__PLEASE_ENTER_YOUR_PASSWORD__'),
                        'minlength': oae.api.i18n.translate('__MSG__YOUR_PASSWORD_SHOULD_BE_AT_LEAST_SIX_CHARACTERS_LONG__')
                    },
                    'accountpreferences-retype-password': {
                        'required': oae.api.i18n.translate('__MSG__PLEASE_REPEAT_YOUR_PASSWORD__'),
                        'passwordmatch': oae.api.i18n.translate('__MSG__THIS_PASSWORD_DOES_NOT_MATCH_THE_FIRST_ONE__')
                    }
                },
                'submitHandler': changePassword
            });

            // Validate the preferences form
            oae.api.util.validation().validate($('#accountpreferences-preferences', $rootel), {
                'submitHandler': updatePreferences
            });
        };

        /**
         * Unsubscribe from emails and show the main view
         */
        var unsubscribeFromEmail = function() {
            unsubscribedFromMail = true;
            showMainView();
        };

        /**
         * Subscribe to emails and show the main view
         */
        var subscribeToEmail = function() {
            unsubscribedFromMail = false;
            $('.oae-large-options-container input[type="radio"][value="weekly"]', $rootel).click();
            showMainView();
        };

        /**
         * Show the widget's main view
         */
        var showMainView = function() {
            $('#accountpreferences-email-unsubscribe-container', $rootel).hide();
            $('#accountpreferences-main-container', $rootel).show();
            $('#accountpreferences-modal-title').text(oae.api.i18n.translate('__MSG__MY_PREFERENCES__', 'accountpreferences'));

            // If the user is not subscribed to email show the subscription on top of the email preferences
            if (unsubscribedFromMail) {
                $('#accountpreferences-email-unsubscribed-modal').show();
                $('#accountpreferences-email-unsubscribed-modal-content').show();
                $('#accountpreferences-email-unsubscribe').hide();
            } else {
                $('#accountpreferences-email-unsubscribed-modal').hide();
                $('#accountpreferences-email-unsubscribed-modal-content').hide();
                $('#accountpreferences-email-unsubscribe').show();
            }
        };

        /**
         * Show the unsubscribe view
         */
        var showUnsubscribeView = function() {
            $('#accountpreferences-main-container', $rootel).hide();
            $('#accountpreferences-email-unsubscribe-container', $rootel).show();
            $('#accountpreferences-modal-title').text(oae.api.i18n.translate('__MSG__UNSUBSCRIBE_FROM_EMAIL_NOTIFICATIONS__', 'accountpreferences'));
        };

        /**
         * Render the list of available languages and select the user's current locale
         * in the dropdowns. The i18n debug language will only be shown to administrators.
         */
        var setUpPreferences = function() {
            // Render the available languages
            oae.api.util.template().render($('#accountpreferences-language-template', $rootel), null, $('#accountpreferences-language', $rootel));
            // Select the current user's language
            $('#accountpreferences-language', $rootel).val(oae.data.me.locale);

            // Render the email preferences
            oae.api.util.template().render($('#accountpreferences-emailpreferences-template', $rootel), null, $('#accountpreferences-emailpreferences-container', $rootel));

            $rootel.on('change', '.oae-large-options-container input[type="radio"]', function() {
                $('.oae-large-options-container label', $rootel).removeClass('checked');
                $(this).parents('label').addClass('checked');
            });
            $rootel.on('click', '#accountpreferences-email-unsubscribe', showUnsubscribeView);
            $rootel.on('click', '#accountpreferences-email-unsubscribe-cancel', showMainView);
            $rootel.on('click', '#accountpreferences-email-unsubscribe-confirm', unsubscribeFromEmail);
            $rootel.on('click', '#accountpreferences-email-subscribe-confirm', subscribeToEmail);
        };

        /**
         * Sets up the accountpreferences modal
         */
        var setUpAccountPreferencesModal = function() {
            $(document).on('click', '.oae-trigger-accountpreferences', function() {
                $('#accountpreferences-modal', $rootel).modal({
                    'backdrop': 'static'
                });
                setUpPreferences();
                setUpValidation();
            });

            $('#accountpreferences-modal', $rootel).on('hidden.bs.modal', reset);
        };

        setUpAccountPreferencesModal();

    };
});
