define(['jquery', 'oae/api/oae.core', 'jquery-plugins/jquery.timeago'], function($, oae) {

    return function(uid, showSettings, widgetData) {


        /////////////////////////////
        // Configuration variables //
        /////////////////////////////

        var $rootel = $('#' + uid);


        ///////////////////////
        // Utility functions //
        ///////////////////////

        /**
         * Renders the activities in the view
         * @param {Object}   data    The activity stream for the current user/group
         */
        var renderActivity = function(data) {
            $('#activity_items_container', $rootel).html(oae.api.util.renderTemplate('#activity_items_template', {'data': data}));
        };

        /**
         * Retrieves a list of the latest 10 activities for the current user/group and triggers the rendering of the activities
         */
        var getActivity = function() {
            $.ajax({
                'url': '/api/activity/' + widgetData.principalId + '?limit=10',
                'success': function(data) {
                    renderActivity(data);
                }
            });
        };


        /////////////////////////
        // Main View functions //
        /////////////////////////



        ////////////////////
        // Event Handlers //
        ////////////////////

        var addBinding = function() {

        };


        /////////////////////////////
        // Initialization function //
        /////////////////////////////

        var doInit = function() {
            addBinding();
            getActivity();
        };

        doInit();
    };
});
