define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * Initialize the annotationsmobile widget
         */
        var initAnnotationsMobile = function() {
            $(document).on('oae.trigger.annotationsmobile', function(ev, annotations, userProfile, contentProfile) {
                $('#annotationsmobile-modal', $rootel).modal({
                    'backdrop': 'static'
                });

                oae.api.util.template().render($('#annotationsmobile-template', $rootel), {
                    'annotations': annotations,
                    'userProfile': userProfile,
                    'contentProfile': contentProfile
                }, $('.modal-body', $rootel));
            });
        };

        initAnnotationsMobile();
    };
});
