define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        // Variable that will be used to keep track of the current infinite scroll instance
        var infinityScroll = null;

        /**
         * Initialize a new infinite scroll container that fetches the members
         *
         * @param  {Content}    contentProfile    Content for which the members should be rendered
         */
        var getMembers = function(contentProfile) {
            // Disable the previous infinite scroll
            if (infinityScroll) {
                infinityScroll.kill();
            }

            var url = '/api/content/'+ contentProfile.id + '/members';

            // Set up the infinite scroll instance
            infinityScroll = $('.oae-list', $rootel).infiniteScroll(url, {
                'limit': 9
            }, '#contentshared-template', {
                'scrollContainer': $('#contentshared-container', $rootel),
                'postProcessor': function(data) {
                    // Map the member's profile directly to `data.results` to
                    // be able to pass it into the listItem macros
                    $.each(data.results, function(i, member) {
                        data.results[i] = _.extend(data.results[i].profile, data.results[i]);
                        delete data.results[i].profile;
                    });
                    data.displayOptions = {
                        'linkTarget': '_blank'
                    };
                    return data;
                }
            });
        };

        /**
         * Initialize the contentshared modal dialog
         */
        var setUpContentShared = function() {
            $(document).on('click', '.oae-trigger-contentshared', function(ev, data) {
                // Request the context profile information
                $(document).trigger('oae.context.get', 'contentshared');
            });

            // Receive the context's profile information and set up the contentshared modal
            $(document).on('oae.context.send.contentshared', function(ev, contentProfile) {
                // Show the contentshared modal
                $('#contentshared-modal', $rootel).modal();
                // Render the members
                getMembers(contentProfile);
            });
        };

        setUpContentShared();

    };
});
