define(['jquery', 'oae.core'], function ($, oae) {

    return function (uid, showSettings) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * Resets the widget to its original state
         */
        var reset = function() {
            $('#createcollabdoc-form')[0].reset();
        };

        /**
         * Binds and executes the widget reset function when the modal dialog is closed
         */
        var setUpReset = function() {
            $('#createcollabdoc-modal', $rootel).on('hidden', function (evt) {
                reset();
            });
        };

        /**
         * Initializes the visibility view and binds an event listener to the
         * change event of the radio group
         */
        var setUpVisibility = function() {
            oae.api.util.template().render($('#createcollabdoc-visibility-template', $rootel), {
                'visibility': oae.api.config.getValue('oae-content', 'visibility', 'collabdocs'),
            }, $('#createcollabdoc-visibility-container', $rootel));

            $('.oae-visibility-choice-container input[type="radio"]', $rootel).on('change', function() {
                $('.oae-visibility-choice', $rootel).removeClass('checked');
                $(this).parents('.oae-visibility-choice').addClass('checked');
            });
        };

        /**
         * Initializes the create collabdoc form and validation
         */
        var setUpDoCreateCollabDoc = function() {
            var validateOpts = {
                'submitHandler': doCreateCollabDoc
            };
            oae.api.util.validation().validate($('#createcollabdoc-form', $rootel), validateOpts);
        };

        /**
         * Creates the collabdoc using the data for title and visibility provided
         */
        var doCreateCollabDoc = function() {
            // Disable the form
            $('#createcollabdoc-form *', $rootel).attr('disabled', 'disabled');

            // Create the document
            var displayName = $('#createcollabdoc-collabdoc-name', $rootel).val();
            var visibility = $('.oae-visibility-choice-container input[type="radio"]:checked', $rootel).val()

            oae.api.content.createCollabDoc(displayName, '', visibility, [], [], function (err, data) {
                // If the creation succeeded redirect to the content
                if (!err) {
                    window.location = '/content/' + data.id;
                } else { // If the creation failed show a notification
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__DOCUMENT_NOT_CREATED__', 'createcollabdoc'),
                        oae.api.i18n.translate('__MSG__DOCUMENT_COULD_NOT_BE_CREATED__', 'createcollabdoc'),
                        'error');
                }
            });

            // Avoid default form submit behavior
            return false;
        };

        /**
         * Initializes the create collabdoc modal dialog
         */
        var setUpCreateCollabDocModal = function() {
            $(document).on('click', '.oae-trigger-createcollabdoc', function() {
                $('#createcollabdoc-modal', $rootel).modal({
                    'backdrop': 'static'
                });
            });

            $('#createcollabdoc-modal', $rootel).on('shown', function() {
                // Set focus to the collabdoc name field
                $('#createcollabdoc-collabdoc-name', $rootel).focus();
                // Initialize the visibility settings
                setUpVisibility();
            });
        };

        setUpCreateCollabDocModal();
        setUpDoCreateCollabDoc();
        setUpReset();

    };
});
