define(['jquery', 'oae.core'], function ($, oae) {

    return function (uid, showSettings) {

        var $rootel = $('#' + uid);
        var visibility = oae.api.config.getValue('oae-content', 'visibility', 'collabdocs');
        var viewers = [];

        /**
         * Initializes the create collabdoc modal dialog
         */
        var initCreateCollabDocModal = function() {
            $(document).on('click', '.oae-trigger-createcollabdoc', function() {
                initSetPermissions();
                $('#createcollabdoc-container').modal({
                    'backdrop': 'static'
                });
            });
        };

        /**
         * Initializes the setpermissions widget in large view
         */
        var initSetPermissions = function() {
            $(document).trigger('oae-trigger-setpermissions', {
                'viewers': [],
                'visibility': visibility,
                'target': $('#createcollabdoc-permissions-container', $rootel)
            });
        };

        /**
         * Triggers the necessary changes when permissions for the files to be uploaded have been changed.
         */
        var initPermissionsChanged = function() {
            $(document).on('oae-trigger-setpermissionschanged', function(ev, data) {
                visibility = data.visibility;
            });
        };

        /**
         * Triggers the necessary changes when members of the files to be uploaded have been changed.
         */
        var initShareChanged = function() {
            $(document).on('oae-trigger-setpermissionsshared', function(ev, data) {
                console.log('viewers changed');
                viewers = data.viewers;
            });
        };

        /**
         * Binds and executes the widget reset function when the modal dialog is closed
         */
        var initReset = function() {
            $('#createcollabdoc-container').on('hidden', function (evt) {
                if ($(evt.target).hasClass('oae-modal')) {
                    doReset();
                }
            });
        };

        /**
         * Resets the widget to its original state
         */
        var doReset = function() {
            // Reset the form
            $('#createcollabdoc-collabdoc-title').val('');
            $('#createcollabdoc-collabdoc-description').val('');

            // Reset the permissions
            visibility = oae.api.config.getValue('oae-content', 'visibility', 'collabdocs');
        };

        /**
         * Initializes the create collabdoc form and validation
         */
        var initDoCreateCollabDoc = function() {
            var validateOpts = {
                'submitHandler': doCreateCollabDoc
            };
            oae.api.util.validation().validate($('#createcollabdoc-form', $rootel), validateOpts);
        };

        /**
         * Creates the collabdoc using the data for title, description and visibility provided
         */
        var doCreateCollabDoc = function() {
            // Disable the form
            $('#createcollabdoc-form *').attr('disabled', 'disabled');

            // Create the document
            var displayName = $('#createcollabdoc-collabdoc-title').val();
            var description = $('#createcollabdoc-collabdoc-description').val();
            oae.api.content.createCollabDoc(displayName, description, visibility, [], viewers, function (success, data) {
                window.location = '/content/' + data.id;
            });
        };

        initCreateCollabDocModal();
        initPermissionsChanged();
        initShareChanged();
        initDoCreateCollabDoc();
        initReset();

    };
});
