define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * Resets the widget to its original state
         */
        var reset = function() {
            $('#creategroup-name').val('');
            $('#creategroup-form *').removeAttr('disabled');
        };

        /**
         * Binds and executes the widget reset function when the modal dialog is closed
         */
        var initReset = function() {
            $('#creategroup-modal').on('hidden', function(ev) {
                reset();
            });
        };

        /**
         * Initializes the visibility view and binds an event listener to the
         * change event of the radio group
         */
        var initVisibility = function() {
            oae.api.util.template().render($('#creategroup-visibility-template', $rootel), {
                'visibility': oae.api.config.getValue('oae-principals', 'group', 'visibility'),
            }, $('#creategroup-visibility-container', $rootel));

            $('.oae-visibility-choice-container input[type="radio"]').on('change', function() {
                $('.oae-visibility-choice', $rootel).removeClass('checked');
                $(this).parents('.oae-visibility-choice').addClass('checked');
            });
        };

        /**
         * Creates the group using the data for name and visibility provided
         */
        var createGroup = function() {
            // Disable the form
            $('#creategroup-form *').attr('disabled', 'disabled');

            // Create the group
            var displayName = $('#creategroup-name').val();
            var alias = oae.api.util.generateId();
            var joinable = oae.api.config.getValue('oae-principals', 'group', 'joinable');
            oae.api.group.createGroup(alias, displayName, '', $('.oae-visibility-choice-container input[type="radio"]:checked').val(), joinable, [], [], function(success, data) {
                window.location = data.profilePath + '/members';
            });
        };

        /**
         * Initializes the create group form and validation
         */
        var setUpValidation = function() {
            var validateOpts = {
                'submitHandler': createGroup
            };
            oae.api.util.validation().validate($('#creategroup-form', $rootel), validateOpts);
        };

        /**
         * Initializes the create group modal dialog
         */
        var initCreateGroupModal = function() {
            $(document).on('click', '.oae-trigger-creategroup', function() {
                initVisibility();
                $('#creategroup-modal').modal({
                    'backdrop': 'static'
                });
                $('#creategroup-modal').on('shown', function () {
                    // Set focus to the group name field
                    $('#creategroup-name').focus();
                });
            });
        };

        initCreateGroupModal();
        setUpValidation();
        initReset();

    };
});
