define(['jquery', 'oae.core'], function($, oae) {

    return function(uid, showSettings) {

        var $rootel = $('#' + uid);

        var visibility = oae.api.config.getValue('oae-principals', 'group', 'visibility');

        /**
         * Resets the widget to its original state
         */
        var doReset = function() {
            // Reset the form
            $('#creategroup-group-title').val('');
            $('#creategroup-form *').removeAttr('disabled');

            // Reset the permissions
            visibility = oae.api.config.getValue('oae-principals', 'group', 'visibility');
        };

        /**
         * Binds and executes the widget reset function when the modal dialog is closed
         */
        var initReset = function() {
            $('#creategroup-container').on('hidden', function(ev) {
                if ($(ev.target).hasClass('oae-modal')) {
                    doReset();
                }
            });
        };

        /**
         * Initializes the setpermissions widget in large view
         */
        var initSetPermissions = function() {
            $(document).trigger('oae-trigger-setpermissions', {
                'viewers': [],
                'visibility': visibility,
                'target': $('#creategroup-permissions-container', $rootel),
                'view': 'large'
            });
        };

        /**
         * Triggers the necessary changes when permissions for the files to be uploaded have been changed.
         */
        var initPermissionsChanged = function() {
            $(document).on('oae-trigger-setpermissionschanged', function(ev, data) {
                visibility = data.visibility;
            });
        };

        /**
         * Creates the group using the data for title and visibility provided
         */
        var doCreateGroup = function() {
            // Disable the form
            $('#creategroup-form *').attr('disabled', 'disabled');

            // Create the group
            var displayName = $('#creategroup-group-title').val();
            var alias = displayName.toLowerCase().replace(/[: ]/g, '-');
            var joinable = oae.api.config.getValue('oae-principals', 'group', 'joinable');
            oae.api.group.createGroup(alias, displayName, '', visibility, joinable, [], [], function(success, data) {
                window.location = '/group/' + data.id;
            });
        };

        /**
         * Checks if a group alias is taken or not
         * @return  {Boolean}   available   Returns false if the group alias has been taken and is unavailable, true if it's unused and available
         */
        var isGroupAliasAvailable = function() {
            var displayName = $('#creategroup-group-title').val();
            var alias = displayName.toLowerCase().replace(/[: ]/g, '-');
            var available = false;
            $.ajax({
                'url': '/api/group/exists/' + alias,
                'async': false,
                'success': function() {
                    available = false;
                }, 'error': function(xhr, textStatus) {
                    available = true;
                }
            });
            return available;
        };

        /**
         * Initializes the create group form and validation
         */
        var initDoCreateGroup = function() {
            var validateOpts = {
                'methods': {
                    'uniquegroupname': {
                        'method': isGroupAliasAvailable,
                        'text': oae.api.i18n.translate('__MSG__THIS_GROUP_HAS_BEEN_TAKEN__', 'creategroup')
                    }
                },
                'submitHandler': doCreateGroup
            };
            oae.api.util.validation().validate($('#creategroup-form', $rootel), validateOpts);
        };

        /**
         * Initializes the create group modal dialog
         */
        var initCreateGroupModal = function() {
            $(document).on('click', '.oae-trigger-creategroup', function() {
                initSetPermissions();
                $('#creategroup-container').modal({
                    'backdrop': 'static'
                });
            });
        };

        initCreateGroupModal();
        initPermissionsChanged();
        initDoCreateGroup();
        initReset();

    };
});
