define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * Resets the widget to its original state when the modal dialog is closed
         */
        var setUpReset = function() {
            $('#creategroup-modal').on('hidden', function() {
                $('#creategroup-form', $rootel)[0].reset();
                $('#creategroup-form *', $rootel).prop('disabled', false);
            });
        };

        /**
         * Initializes the visibility options and binds an event listener to the
         * change event of the radio group
         */
        var setUpVisibility = function() {
            oae.api.util.template().render($('#creategroup-visibility-template', $rootel), {
                'visibility': oae.api.config.getValue('oae-principals', 'group', 'visibility')
            }, $('#creategroup-visibility-container', $rootel));

            $rootel.on('change', '.oae-visibility-choice-container input[type="radio"]', function() {
                $('.oae-visibility-choice', $rootel).removeClass('checked');
                $(this).parents('.oae-visibility-choice').addClass('checked');
            });
        };

        /**
         * Creates the group using the provided group name and selected group visibility
         */
        var createGroup = function() {
            // Disable the form
            $('#creategroup-form *', $rootel).prop('disabled', true);

            // Create the group
            var displayName = $('#creategroup-name', $rootel).val();
            var visibility = $('.oae-visibility-choice-container input[type="radio"]:checked', $rootel).val();
            oae.api.group.createGroup(displayName, '', visibility, null, [], [], function(success, data) {
                // Redirect to the members page so users can start adding member straight away
                // instead of showing an empty activity feed
                window.location = data.profilePath + '/members';
            });
        };

        /**
         * Initializes the create group form and validation
         */
        var setUpValidation = function() {
            oae.api.util.validation().validate($('#creategroup-form', $rootel), {
                'submitHandler': createGroup
            });
        };

        /**
         * Initializes the create group modal dialog
         */
        var setUpCreateGroupModal = function() {
            $(document).on('click', '.oae-trigger-creategroup', function() {
                setUpVisibility();
                $('#creategroup-modal', $rootel).modal();
                $('#creategroup-modal', $rootel).on('shown', function () {
                    // Set focus to the group name field
                    $('#creategroup-name', $rootel).focus();
                });
            });
        };

        setUpValidation();
        setUpReset();
        setUpCreateGroupModal();

    };
});
