casper.test.comment('Widget - Create link');

/**
 * Open the createlink modal with assertions
 */
var verifyOpenCreateLink = function() {
    // Wait till the widget loading mechanisme is ready
    // Do this by waiting till a template has been rendered
    casper.waitForSelector('#me-clip-container .oae-clip', function() {
        casper.waitForSelector('#me-clip-container .oae-clip-content > button', function() {
            casper.click('#me-clip-container .oae-clip-content > button');
            casper.test.assertExists('.oae-trigger-createlink', 'Createlink trigger exists');
            casper.click('.oae-trigger-createlink');
            casper.wait(3000, function() {
                casper.test.assertVisible('#createlink-modal', 'Createlink pane is showing after trigger');
                casper.click('#me-clip-container .oae-clip-content > button');
            });
        });
    });
};

/**
 * Open the createlink modal
 */
var openCreateLink = function() {
    // Wait till the widget loading mechanisme is ready
    // Do this by waiting till a template has been rendered
    casper.waitForSelector('#me-clip-container .oae-clip', function() {
        casper.waitForSelector('#me-clip-container .oae-clip-content > button', function() {
            casper.click('#me-clip-container .oae-clip-content > button');
            casper.click('.oae-trigger-createlink');
            casper.wait(3000, function() {
                casper.click('#me-clip-container .oae-clip-content > button');
            });
        });
    });
};

/**
 * Verify creating a single link
 */
var verifyCreateSingleLink = function() {
    // Verify the link dump area is there
    casper.test.assertExists('#createlink-link-dump', 'The link dump area is present');
    // Add a single link to the textarea
    casper.sendKeys('#createlink-link-dump', 'http://www.oaeproject.org');
    // TODO: The casper events aren't properly sent out so the validation will not be triggered.
    //       This causes the next button to remain disabled.
    //       We're removing this state manually for now so we can continue the test.
    casper.evaluate(function() {
        document.getElementById('createlink-next').removeAttribute('disabled');
    });
    // Verify the 'Next' button is present
    casper.test.assertExists('#createlink-next', 'The \'Next\' button is present');
    // Verify the 'Next' button is enabled
    casper.test.assertDoesntExist('#createlink-next:disabled', 'The \'Next\' button is enabled');
    // Go to the next step
    casper.click('#createlink-next');
    // Add the link
    casper.test.assertExists('button#createlink-create', 'The \'Add link(s)\' button is present');
    casper.click('button#createlink-create');
    // Verify that the link has been uploaded by going to the content profile provided in the notification
    casper.waitForSelector('#oae-notification-container .alert', function() {
        casper.test.assertDoesntExist('#oae-notification-container .alert.alert-error', 'Link successfully added');
        // Click the first link in the notification message to go to the content profile
        casper.test.assertExists('#oae-notification-container .alert h4 + a', 'The link to the content profile is shown in the notification');
        casper.test.assertSelectorHasText('#oae-notification-container .alert h4 + a', 'http://www.oaeproject.org', 'Notification link value is \'http://www.oaeproject.org\'');
        casper.click('#oae-notification-container .close');
    });
};

/**
 * Verify creating multiple links
 */
var verifyCreateMultipleLinks = function() {
    // Verify the link dump area is there
    casper.test.assertExists('#createlink-link-dump', 'The link dump area is present');
    // Add a couple of links to the textarea
    casper.sendKeys('#createlink-link-dump', 'http://www.oaeproject.org\nhttp://www.google.com');
    // TODO: The casper events aren't properly sent out so the validation will not be triggered.
    //       This causes the next button to remain disabled.
    //       We're removing this state manually for now so we can continue the test.
    casper.evaluate(function() {
        document.getElementById('createlink-next').removeAttribute('disabled');
    });
    // Verify the 'Next' button is present
    casper.test.assertExists('#createlink-next', 'The \'Next\' button is present');
    // Verify the 'Next' button is enabled
    casper.test.assertDoesntExist('#createlink-next:disabled', 'The \'Next\' button is enabled');
    // Go to the next step
    casper.click('#createlink-next');
    // Add the links
    casper.test.assertExists('button#createlink-create', 'The \'Add link(s)\' button is present');
    casper.click('button#createlink-create');
    // Verify that the links have been created
    casper.waitForSelector('#oae-notification-container .alert', function() {
        casper.test.assertDoesntExist('#oae-notification-container .alert.alert-error', 'Links successfully created');
        casper.click('#oae-notification-container .close');
    });
};

/**
 * Verify renaming a link
 */
var verifyRenameLink = function() {
    // Add a link to the textarea
    casper.sendKeys('#createlink-link-dump', 'http://www.oaeproject.org');
    // TODO: The casper events aren't properly sent out so the validation will not be triggered.
    //       This causes the next button to remain disabled.
    //       We're removing this state manually for now so we can continue the test.
    casper.evaluate(function() {
        document.getElementById('createlink-next').removeAttribute('disabled');
    });
    // Go to the next step
    casper.click('#createlink-next');
    // Verify that the jeditable field is present
    casper.test.assertExists('#createlink-modal .createlink-selected-name', 'The editable link name field is present');
    casper.test.assertSelectorHasText('#createlink-modal .createlink-selected-name', 'http://www.oaeproject.org', 'Selected link has name \'http://www.oaeproject.org\'');
    // CasperJS has an issue clicking elements that have tabindex="0" specified, remove it.
    casper.evaluate(function() {
        document.getElementsByClassName('createlink-selected-name')[0].removeAttribute('tabindex');
    });
    casper.wait(2000, function() {
        casper.click('.createlink-selected-name');

        casper.waitForSelector('#createlink-modal .createlink-selected-name form', function() {
            casper.test.assertExists('#createlink-modal .createlink-selected-name form', 'The link name form is present after click');

            // fill the form
            casper.fill('#createlink-modal .createlink-selected-name form', {
                'value': 'OAE Project'
            }, false);
            casper.click('html');
            // Verify that the new name is shown in the list
            casper.test.assertSelectorHasText('.createlink-selected-name', 'OAE Project', 'Renamed link has name \'OAE Project\'');
            // Add the link
            casper.click('button#createlink-create');
            // Verify that the link has been created by going to the content profile provided in the notification
            casper.waitForSelector('#oae-notification-container .alert', function() {
                casper.test.assertDoesntExist('#oae-notification-container .alert.alert-error', 'Link successfully created');
                // Click the first link in the notification message to go to the content profile
                casper.test.assertExists('#oae-notification-container .alert h4 + a', 'The link to the content profile is shown in the notification');
                casper.test.assertSelectorHasText('#oae-notification-container .alert h4 + a', 'OAE Project', 'Notification link value is \'OAE Project\'');
                casper.click('#oae-notification-container .alert h4 + a');
                // Wait a couple of seconds for the pageload
                casper.wait(2000, function() {
                    // Verify that the new name is used for the link
                    casper.test.assertSelectorHasText('#content-clip-container h1', 'OAE Project', 'The created link has the correct renamed title');
                });
            });
        });
    });
};

casper.start('http://test.oae.com', function() {
    // Create a couple of users to test createlink with
    var user1 = null;
    userUtil().createUsers(1, function(users) {
        user1 = users[0];
    });

    // Login with that user
    casper.then(function() {
        userUtil().doLogIn(user1.username, 'password');
    });

    // Open the createlink modal
    casper.then(function() {
        casper.echo('Verify createlink modal', 'INFO');
        verifyOpenCreateLink();
    });

    // Verify creating a link
    casper.then(function() {
        casper.echo('Verify creating single link', 'INFO');
        verifyCreateSingleLink();
    });

    // Verify creating multiple links
    casper.then(function() {
        casper.echo('Verify creating multiple links', 'INFO');
        casper.then(openCreateLink);
        casper.then(verifyCreateMultipleLinks);
    });

    // Verify renaming link
    casper.thenOpen('http://test.oae.com/me', function() {
        casper.echo('Verify renaming link', 'INFO');
        casper.then(openCreateLink);
        casper.then(verifyRenameLink);
    });

    // Log out at the end of the test
    casper.then(function() {
        userUtil().doLogOut();
    });
});

casper.run(function() {
    this.test.done();
});
