define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * Initialize the delete annotation widget
         */
        var initDeleteAnnotation = function() {
            $(document).on('click', '.oae-trigger-deleteannotation', function(ev) {
                $('#deleteannotation-modal', $rootel).modal({
                    'backdrop': 'static'
                });

                // Add the annotation to delete as data on the delete button so the Sidebar
                // plugin will pick it up once clicked
                var annotationToDelete = $(ev.currentTarget).closest('li').data('annotation');
                $('#deleteannotation-delete', $rootel).data('annotation', annotationToDelete);
            });

            $(window).on('oae.deleteannotation.done', function() {
                oae.api.util.notification(
                    oae.api.i18n.translate('__MSG__ANNOTATION_DELETED__', 'deleteannotation'),
                    oae.api.i18n.translate('__MSG__ANNOTATION_DELETE_SUCCESS__', 'deleteannotation'));

                // Hide the modal
                $('#deleteannotation-modal', $rootel).modal('hide');
            });
        };

        initDeleteAnnotation();
    };
});
