define(['jquery', 'oae.core', 'jquery.history'], function($, oae) {

    return function(uid, showSettings, widgetData) {

        // The widget container
        var $rootel = $('#' + uid);

        // Variable that will be used to keep track of the current infinite scroll instance
        var infinityScroll = false;

        /**
         * Initialize a new infinite scroll container that fetches a discussion library.
         * This will detect when a search is happening and will change the endpoint
         * accordingly.
         */
        var getDiscussionsLibrary = function() {
            // Disable the previous infinite scroll
            if (infinityScroll) {
                infinityScroll.kill();
            }

            // Detect whether or not we need to do a search by checking if
            // the History.js state has a query parameter
            var query = History.getState().data.query;
            $('#discussionslibrary-search-query', $rootel).val(query);

            // Set up the list actions
            var initialContent = null;
            if (widgetData.canManage && !query) {
                initialContent = oae.api.util.template().render($('#discussionslibrary-list-actions-template', $rootel));
            }

            var url = '/api/discussion/library/' + widgetData.principalId;
            if (query) {
                url = '/api/search/discussion-library/' + widgetData.principalId;
            }

            // Set up the infinite scroll for the discussions library
            infinityScroll = $('.oae-list', $rootel).infiniteScroll(url, {
                'limit': 12,
                'q': query
            }, '#discussionslibrary-template', {
                'initialContent': initialContent,
                'postProcessor': function(data) {
                    // Let the template know whether or not the current list
                    // is a main list or a search list, as different paging
                    // keys need to be provided for each
                    data.query = query;
                    return data;
                },
                'emptyListProcessor': function() {
                    oae.api.util.template().render($('#discussionslibrary-noresults-template', $rootel), {
                        'query': query
                    }, $('.oae-list', $rootel));
                }
            });
        };

        /**
         * If the current user is an anonymous user, we disable the toggle that displays
         * the different list item actions. If the user is logged in, we render the list of
         * available action based on whether or not the user can manage this library.
         */
        var setUpListOptions = function() {
            if (oae.data.me.anon) {
                $('.oae-list-options-toggle', $rootel).prop('disabled', true);
            } else {
                oae.api.util.template().render($('#discussionlibrary-list-options-actions-template', $rootel), {
                    'widgetData': widgetData
                }, $('.oae-list-options-actions', $rootel));
            }
        };

        /**
         * Initiate a search with the search query entered by the user. An empty
         * search will just show the normal list.
         */
        var search = function() {
            var query = $('#discussionslibrary-search-query', $(this)).val();
            // Push the new query to a new History.js state. We make sure to take the
            // existing state data parameters with us and construct a new URL based on
            // the existing base URL, allowing for page refreshing and bookmarking.
            var newState = $.extend({}, History.getState().data, {
                'query': query
            });

            // We cannot rely on the "current" url as that can be different depending on the browser.
            // Most browsers will display `/me/discussions`, IE9 will be display `/me#discussions` however.
            // The cleanUrl in the History.js state will always be `/me/discussions`.
            var url = $.url(History.getState().cleanUrl).attr('path') + '?q=' + oae.api.util.security().encodeForURL(query);
            History.pushState(newState, $('title').text(), url);

            // Avoid that the search form is being submitted
            return false;
        };

        /**
         * Add the different event bindings
         */
        var addBinding = function() {
            // Set up search event
            $rootel.on('submit', '#discussionslibrary-search-form', search);

            // Listen to History.js state changes
            $(window).on('statechange', function() {
                // Only re-load the discussion list when the widget is currently visible
                if ($rootel.is(':visible')) {
                    getDiscussionsLibrary();
                }
            });

            // Listen to the event that indicates that a piece of content has been deleted
            // so the library can be reloaded
            $(window).on('oae.deleteresources.done', function() {
                // Only re-load the discussion list when the widget is currently visible
                if ($rootel.is(':visible')) {
                    getDiscussionsLibrary();
                    // Make sure the Select All checkbox is unchecked
                    $('#discussionslibrary-selectall', $rootel).prop('checked', false);
                }
            });
        };

        addBinding();
        setUpListOptions();
        getDiscussionsLibrary();

    };
});
