casper.test.comment('Widget - Discussion library');

/**
 * Verify that the share widget can be triggered from within the list options
 */
var verifyDiscussionsLibraryShare = function() {
    // Verify that the share button is disabled by default
    casper.test.assertExists('#discussionslibrary-widget .oae-list-options button.oae-trigger-share:disabled', 'The share button is disabled by default');
    // Select the first discussion item in the list
    casper.click('li.oae-list-actions + li .oae-list-grid-item input[type="checkbox"]');
    // Verify that the share button is now enabled
    casper.test.assertExists('#discussionslibrary-widget .oae-list-options button.oae-trigger-share:not([disabled])', 'The share button is enabled after checking a discussion item');
    // Click the share button
    casper.click('#discussionslibrary-widget .oae-list-options .oae-trigger-share');
    // Verify that the share popover is shown
    casper.waitForSelector('#share-container', function() {
        casper.test.assertExists('#share-container', 'The share popover can be triggered from within the list options');
        // Uncheck the first item from the list
        casper.click('li.oae-list-actions + li .oae-list-grid-item input[type="checkbox"]');
        // Verify that the share button is disabled again
        casper.test.assertExists('#discussionslibrary-widget .oae-list-options button.oae-trigger-share:disabled', 'The share button is disabled again after unchecking all discussion items');
    });
};

/**
 * Verify that the deleteresources widget can be triggered from within the list options
 */
var verifyDiscussionsLibraryDelete = function() {
    casper.waitForSelector('li.oae-list-actions + li .oae-list-grid-item input[type="checkbox"]', function() {
        // Verify that delete button is disabled by default
        casper.test.assertExists('#discussionslibrary-widget .oae-list-options button.oae-trigger-deleteresources:disabled', 'The delete button is disabled by default');
        // Select the first discussion item in the list
        casper.click('li.oae-list-actions + li .oae-list-grid-item input[type="checkbox"]');
        // Verify that the delete button is now enabled
        casper.test.assertExists('#discussionslibrary-widget .oae-list-options button.oae-trigger-deleteresources:not([disabled])', 'The delete button is enabled after checking a discussion item');
        // Click the delete button
        casper.click('#discussionslibrary-widget .oae-list-options button.oae-trigger-deleteresources');
        // Verify that the deleteresources widget modal is shown
        casper.waitForSelector('#deleteresources-modal', function() {
            casper.test.assertExists('#deleteresources-modal', 'The deleteresources modal can be triggered from within the list options');
            // Close the modal
            casper.click('#deleteresources-modal .close');
            // Uncheck the first item from the list
            casper.click('li.oae-list-actions + li .oae-list-grid-item input[type="checkbox"]');
            // Verify that the delete button is disabled again
            casper.test.assertExists('#discussionslibrary-widget .oae-list-options button.oae-trigger-deleteresources:disabled', 'The delete button is disabled again after unchecking all discussion items');
        });
    });
};

/**
 * Verify that the discussion library can be searched
 */
var verifyDiscussionsLibrarySearch = function(discussion) {
    // Search for 'noissucsid'
    casper.fill('form.form-search', {
        'search-query': 'noissucsid'
    }, false);
    casper.click('form.form-search button[type="submit"]');
    // Verify that no results come back and a message is shown
    casper.waitForSelector('.oae-list .alert-info', function() {
        casper.test.assertExists('.oae-list .alert-info', 'No results are returned for \'noissucsid\' and a message is shown');
        // Search for 'conversation' which should return 1 result
        casper.fill('form.form-search', {
            'search-query': discussion.displayName
        }, false);
        casper.click('form.form-search button[type="submit"]');
        // Verify one search result comes back
        casper.waitForSelector('#discussionslibrary-widget .oae-list li', function() {
            casper.test.assertExists('#discussionslibrary-widget  .oae-list li', '1 result is returned for \'' + discussion.displayName + '\'');
            casper.test.assertSelectorHasText('#discussionslibrary-widget .oae-list li .oae-tile-metadata a', discussion.displayName, 'The returned discussion has the title \'' + discussion.displayName + '\'');
            // Reset the form
            casper.fill('form.form-search', {
                'search-query': ''
            }, false);
            casper.click('form.form-search button[type="submit"]');
        });
    });
};

/**
 * Verify that the view mode can be changed through the list options
 */
var verifyDiscussionsLibraryViewMode = function() {
    // Toggle the list options
    casper.click('.oae-list-options-toggle');
    // Verify compact list
    casper.click('#discussionslibrary-widget .oae-list-options button[data-type="oae-list-compact"]');
    casper.test.assertExists('.oae-list.oae-list-compact', 'Discussion library can be switched to compact view');
    // Verify details list
    casper.click('#discussionslibrary-widget .oae-list-options button[data-type="oae-list-details"]');
    casper.test.assertExists('.oae-list.oae-list-details', 'Discussion library can be switched to details view');
    // Switch back to grid view
    casper.click('#discussionslibrary-widget .oae-list-options button[data-type="oae-list-grid"]');
    casper.test.assertExists('.oae-list.oae-list-grid', 'Discussion library can be switched to grid view');
};

/**
 * Verify if all elements are present in the discussion library
 */
var verifyDiscussionsLibraryElements = function() {
    // Verify there is a dummy list item with action buttons
    casper.test.assertExists('#discussionslibrary-widget .oae-list li:first-child', 'Initial dummy list item is present');
    // Verify dummy list item has start discussion button
    casper.test.assertExists('#discussionslibrary-widget .oae-list li:first-child .oae-trigger-creatediscussion', 'The first list item has a \'Start discussion\' trigger');
    // Verify create discussion button triggers creatediscussion widget
    casper.click('#discussionslibrary-widget .oae-list li:first-child .oae-trigger-creatediscussion');
    casper.waitForSelector('#creatediscussion-modal', function() {
        casper.test.assertExists('#creatediscussion-modal #creatediscussion-modal-title', 'The creatediscussion widget can be triggered from the dummy list item');
        casper.click('#creatediscussion-modal .close');
    });
    // Verify list options are there
    casper.test.assertExists('#discussionslibrary-widget .oae-list-options', 'The list options are present');
    // Verify list options contain a checkbox that selects all
    casper.test.assertExists('#discussionslibrary-widget .oae-list-options input[type="checkbox"]', 'The \'Select all\' checkbox is present in the list options');
    // Verify list options contain a share button
    casper.test.assertExists('#discussionslibrary-widget .oae-list-options .oae-trigger-share', 'The share button is present in the list options');
    // Verify list options contain a delete button
    casper.test.assertExists('#discussionslibrary-widget .oae-list-options .oae-trigger-deleteresources', 'The delete button is present in the list options');
    // Verify list options contain a switch view button
    casper.test.assertExists('#discussionslibrary-widget .oae-list-options button[data-type="oae-list-compact"]', 'The \'Compact\' list view button is present');
    casper.test.assertExists('#discussionslibrary-widget .oae-list-options button[data-type="oae-list-details"]', 'The \'Details\' list view button is present');
    casper.test.assertExists('#discussionslibrary-widget .oae-list-options button[data-type="oae-list-grid"]', 'The \'Grid\' list view button is present');
    // Verify list options contain a search field
    casper.test.assertExists('#discussionslibrary-widget .oae-list-options #discussionslibrary-search-query', 'The search box is present');
};

casper.start('http://test.oae.com', function() {
    var discussion = null;
    var user1 = null;

    // Create a couple of users to test with
    userUtil().createUsers(1, function(users) {
        user1 = users[0];
    });

    // Login with that user
    casper.then(function() {
        userUtil().doLogIn(user1.username, 'password');
    });

    casper.thenOpen('http://test.oae.com/me/discussions', function() {
        discussionUtil().createDiscussion(function(_discussion) {
            discussion = _discussion;
            casper.then(function() {
                casper.echo('Verify discussion library elements present', 'INFO');
                casper.waitForSelector('div[data-id="discussions"]', verifyDiscussionsLibraryElements);
            });

            casper.then(function() {
                casper.echo('Verify discussion library view modes', 'INFO');
                verifyDiscussionsLibraryViewMode();
            });

            casper.then(function() {
                casper.echo('Verify discussion library delete', 'INFO');
                verifyDiscussionsLibraryDelete();
            });

            casper.then(function() {
                casper.echo('Verify discussion library share', 'INFO');
                verifyDiscussionsLibraryShare();
            });

            casper.then(function() {
                casper.echo('Verify discussion library search', 'INFO');
                verifyDiscussionsLibrarySearch(discussion);
            });
        });
    });

    // Log out at the end of the test
    casper.then(function() {
        userUtil().doLogOut();
    });
});

casper.run(function() {
    this.test.done();
});
