casper.test.begin('Widget - Edit content', function(test) {

    /**
     * Open the edit content modal with assertions
     */
    var verifyOpenEditContentModal = function() {
        casper.waitForSelector('#content-clip-container .oae-clip-content > button', function() {
            casper.click('#content-clip-container .oae-clip-content > button');
            casper.waitForSelector('button.oae-trigger-editcontent', function() {
                casper.test.assertExists('button.oae-trigger-editcontent', 'The edit content trigger is present');
                casper.click('button.oae-trigger-editcontent');
                casper.waitUntilVisible('#editcontent-modal', function() {
                    casper.test.assertVisible('#editcontent-modal', 'The edit content pane is showing after trigger');
                    casper.click('#content-clip-container .oae-clip-content > button');
                });
            });
        });
    };

    /**
     * Open the edit content modal without assertions
     */
    var openEditContentModal = function() {
        casper.waitForSelector('#content-clip-container .oae-clip-content > button', function() {
            casper.click('#content-clip-container .oae-clip-content > button');
            casper.waitForSelector('button.oae-trigger-editcontent', function() {
                casper.click('button.oae-trigger-editcontent');
                casper.waitUntilVisible('#editcontent-modal', function() {
                    casper.click('#content-clip-container .oae-clip-content > button');
                });
            });
        });
    };

    /**
     * Verify that the edit content elements are present
     */
    var verifyEditContentElements = function() {
        casper.test.assertExists('#editcontent-modal #editcontent-form', 'Verify that the edit content form is present');
        casper.test.assertExists('#editcontent-modal #editcontent-form input#editcontent-name', 'Verify that the content name field is present');
        casper.test.assertExists('#editcontent-modal #editcontent-form textarea#editcontent-description', 'Verify that the content description field is present');
        casper.test.assertExists('#editcontent-modal #editcontent-form button[data-dismiss="modal"]', 'Verify that the content cancel button present');
        casper.test.assertExists('#editcontent-modal #editcontent-form button[type="submit"]', 'Verify that the content `Edit content` button present');
    };

    /**
     * Verify the form validation when editing content
     */
    var verifyEditContentFormValidation = function() {
        // Try submitting an empty form
        casper.fill('#editcontent-modal #editcontent-form', {
            'editcontent-name': '',
            'editcontent-description': ''
        }, false);
        casper.click('#editcontent-modal #editcontent-form button[type="submit"]');
        casper.test.assertExists('#editcontent-modal #editcontent-form #editcontent-name-error', 'Empty form content name is properly validated');
        casper.test.assertDoesntExist('#editcontent-modal #editcontent-form #editcontent-description-error', 'Empty form description is properly validated');

        // Try submitting a form with only a description
        casper.fill('#editcontent-modal #editcontent-form', {
            'editcontent-name': '',
            'editcontent-description': 'Description'
        }, false);
        casper.click('#editcontent-modal #editcontent-form button[type="submit"]');
        casper.test.assertExists('#editcontent-modal #editcontent-form #editcontent-name-error', 'Form content name is properly validated when only submitting a description');
        casper.test.assertDoesntExist('#editcontent-modal #editcontent-form #editcontent-description-error', 'Empty form content description is properly validated when only submitting a description');

        // Try submitting spaces only
        casper.fill('#editcontent-modal #editcontent-form', {
            'editcontent-name': '       ',
            'editcontent-description': '       '
        }, false);
        casper.click('#editcontent-modal #editcontent-form button[type="submit"]');
        casper.test.assertExists('#editcontent-modal #editcontent-form #editcontent-name-error', 'Form content name is properly validated when only submitting spaces');
        casper.test.assertDoesntExist('#editcontent-modal #editcontent-form #editcontent-description-error', 'Empty form content description is properly validated when only submitting spaces');
    };

    /**
     * Verify that content can be edited
     */
    var verifyEditContent = function() {
        // Try submitting an empty form
        casper.fill('#editcontent-modal #editcontent-form', {
            'editcontent-name': 'New content name',
            'editcontent-description': 'Content description'
        }, false);
        casper.click('#editcontent-modal #editcontent-form button[type="submit"]');
        // Verify that the content was edited
        casper.waitForSelector('#oae-notification-container .alert', function() {
            casper.test.assertDoesntExist('#oae-notification-container .alert.alert-error', 'Verify that editing content succeeds');
            casper.test.assertSelectorHasText('#content-clip-container h1', 'New content name', 'The content was successfully renamed to \'New content name\'');
            casper.click('#oae-notification-container .close');
        });
    };

    casper.start('http://test.oae.com', function() {
        // Create a user to test with
        var user = null;
        userUtil().createUsers(1, function(users) {
            user = users[0];
        });

        // Login with that user
        casper.then(function() {
            userUtil().doLogIn(user.username, 'password');
        });

        casper.then(function() {
            contentUtil().createLink(null, null, null, function(link) {
                casper.thenOpen('http://test.oae.com' + link.profilePath, function() {
                    casper.then(function() {
                        casper.echo('Verify open edit content modal', 'INFO');
                        verifyOpenEditContentModal();
                    });

                    casper.then(function() {
                        casper.echo('Verify edit content elements', 'INFO');
                        verifyEditContentElements();
                    });

                    casper.then(function() {
                        casper.echo('Verify edit content form validation', 'INFO');
                        verifyEditContentFormValidation();
                    });

                    casper.then(function() {
                        casper.echo('Verify content can be edited', 'INFO');
                        verifyEditContent();
                    });
                });
            });
        });

        // Log out at the end of the test
        casper.then(function() {
            userUtil().doLogOut();
        });
    });

    casper.run(function() {
        this.test.done();
    });
});
