define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        // Caches the group profile
        var groupProfile = null;

        /**
         * Initializes the joinability options and binds an event listener to the
         * change event of the radio group
         */
        var setUpJoinability = function() {
            oae.api.util.template().render($('#editgroup-joinable-template', $rootel), {
                'joinable': groupProfile.joinable
            }, $('#editgroup-joinable-container', $rootel));

            $rootel.on('change', '.oae-large-options-container input[type="radio"]', function() {
                $('.oae-large-options', $rootel).removeClass('checked');
                $('.oae-large-options i', $rootel).removeClass('selected');
                $(this).parents('.oae-large-options').addClass('checked');
                $(this).parents('.oae-large-options').find('i').addClass('selected');
            });
        };

        /**
         * Updates the group with the provided group name, description and joinability setting
         */
        var editGroup = function() {
            // Disable the form
            $('#editgroup-form *', $rootel).prop('disabled', true);

            var params = {
                'displayName': $.trim($('#editgroup-name', $rootel).val()),
                'description': $.trim($('#editgroup-description', $rootel).val()),
                'joinable': $('.oae-large-options-container input[type="radio"]:checked', $rootel).val()
            };

            // Edit the group
            oae.api.group.updateGroup(groupProfile.id, params, function(err, data) {
                $('#editgroup-form *', $rootel).prop('disabled', false);
                if (!err) {
                    $('#editgroup-modal', $rootel).modal('hide');
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__GROUP_EDITED__', 'editgroup'),
                        oae.api.i18n.translate('__MSG__GROUP_EDIT_SUCCESS__', 'editgroup'),
                        'success');

                    $(document).trigger('oae.editgroup.done', data);
                } else {
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__GROUP_NOT_EDITED__', 'editgroup'),
                        oae.api.i18n.translate('__MSG__GROUP_EDIT_FAIL__', 'editgroup'),
                        'error');
                }
            });
        };

        /**
         * Renders the edit group form and initializes the validation.
         */
        var setUpValidation = function() {
            oae.api.util.template().render($('#editgroup-metadata-template', $rootel), {
                'group': groupProfile
            }, $('#editgroup-metadata-container', $rootel));

            oae.api.util.validation().validate($('#editgroup-form', $rootel), {
                'submitHandler': editGroup
            });
        };

        /**
         * Resets the widget to its original state when the modal dialog is closed
         */
        var setUpReset = function() {
            $('#editgroup-modal').on('hidden', function() {
                // Reset the form
                var $form = $('#editgroup-form', $rootel);
                $form[0].reset();
                oae.api.util.validation().clear($form);
                // Disable the submit button
                $('#editgroup-form button[type="submit"]', $rootel).prop('disabled', true);
            });
        };

        /**
         * Initializes the edit group modal dialog
         */
        var setUpEditGroupModal = function() {
            $(document).on('click', '.group-trigger-editgroup', function() {
                $('#editgroup-modal', $rootel).modal({
                    'backdrop': 'static'
                });
                $(document).trigger('oae.context.get', 'editgroup');
            });

            $(document).on('oae.context.send.editgroup', function(ev, data) {
                groupProfile = data;
                setUpJoinability();
                setUpValidation();
            });

            // Detect changes in the form and enable the submit button
            $('#editgroup-form', $rootel).on('change keyup paste', function() {
                $('#editgroup-form button[type="submit"]', $rootel).prop('disabled', false);
            });

            $('#editgroup-modal', $rootel).on('shown', function () {
                // Set focus to the group name field
                $('#editgroup-name', $rootel).focus();
            });
        };

        setUpReset();
        setUpEditGroupModal();

    };
});
