define(['jquery', 'oae.core'], function ($, oae) {

    return function (uid) {

        // The widget container
        var $rootel = $('#' + uid);

        // Caches the user profile
        var userProfile = null;

        /**
         * Renders the edit profile form and initializes validation
         */
        var setUpEditProfile = function() {
            // Render the form elements
            oae.api.util.template().render($('#editprofile-template', $rootel), {
                'userProfile': userProfile
            }, $('.modal-body', $rootel));

            // Initialize jQuery validate on the form
            var validateOpts = {
                'submitHandler': editProfile
            };
            oae.api.util.validation().validate($('#editprofile-form', $rootel), validateOpts);
        };

        /**
         * Edits the profile
         */
        var editProfile = function() {
            // Disable the form
            $('#editprofile-form *', $rootel).prop('disabled', true);

            var params = {
                'displayName': $.trim($('#editprofile-name', $rootel).val()),
                'email': $.trim($('#editprofile-email', $rootel).val()),
                'visibility': $('.oae-large-options-container input[type="radio"]:checked', $rootel).val()
            };

            oae.api.user.updateUser(params, function (err, data) {
                var notificationBody = oae.api.util.template().render($('#editprofile-notification-template'), {
                    'err': err
                });

                if (!err) {
                    $('#editprofile-modal', $rootel).modal('hide');
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__PROFILE_EDITED__', 'editprofile'),
                        notificationBody);
                    $(document).trigger('oae.editprofile.done', data);
                } else {
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__PROFILE_NOT_EDITED__', 'editprofile'),
                        notificationBody,
                        'error');
                        // Enable the form
                        $('#editprofile-form *', $rootel).prop('disabled', false);
                }
            });

            // Avoid default form submit behavior
            return false;
        };

        /**
         * Resets the widget to its original state when the modal dialog is closed
         */
        var setUpReset = function() {
            $('#editprofile-modal', $rootel).on('hidden', function (evt) {
                // Reset the form
                var $form = $('#editprofile-form', $rootel);
                $form[0].reset();
                oae.api.util.validation().clear($form);
                // Enable the form
                $('#editprofile-form *', $rootel).prop('disabled', false);
            });
        };

        /**
         * Initializes the edit profile modal dialog
         */
        var setUpEditProfileModal = function() {
            $(document).on('click', '.oae-trigger-editprofile', function() {
                $('#editprofile-modal', $rootel).modal();
                $(document).trigger('oae.context.get', 'editprofile');
            });

            $(document).on('oae.context.send.editprofile', function(ev, data) {
                userProfile = data;
                setUpEditProfile();
            });

            // Catch changes in the visibility radio group
            $rootel.on('change', '.oae-large-options-container input[type="radio"]', function() {
                $('.oae-large-options', $rootel).removeClass('checked');
                $(this).parents('.oae-large-options').addClass('checked');
            });

            $('#editprofile-modal', $rootel).on('shown', function() {
                // Set focus to the display name field
                $('#editprofile-name', $rootel).focus();
            });
        };

        setUpReset();
        setUpEditProfileModal();

    };
});
