define(['jquery', 'oae.core'], function ($, oae) {

    return function (uid) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * Sets up validation on the edit profile form
         */
        var setUpValidation = function() {
            var validateOpts = {
                'submitHandler': editProfile
            };
            oae.api.util.validation().validate($('#editprofile-form', $rootel), validateOpts);
        };

        /**
         * Renders the edit profile form and initializes validation
         */
        var setUpEditProfile = function() {
            // Render the form elements
            oae.api.util.template().render($('#editprofile-template', $rootel), null, $('.modal-body', $rootel));
            setUpValidation();
        };

        /**
         * Submits the edited profile and shows a success or failure notification
         */
        var editProfile = function() {
            // Disable the form
            $('#editprofile-form *', $rootel).prop('disabled', true);

            var params = {
                'displayName': $.trim($('#editprofile-name', $rootel).val()),
                'email': $.trim($('#editprofile-email', $rootel).val()),
                'visibility': $('.oae-large-options-container input[type="radio"]:checked', $rootel).val()
            };

            oae.api.user.updateUser(params, function (err, data) {
                if (!err) {
                    $('#editprofile-modal', $rootel).modal('hide');
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__PROFILE_EDITED__'),
                        oae.api.i18n.translate('__MSG__PROFILE_DETAILS_EDIT_SUCCESS__', 'editprofile'));
                    $(document).trigger('oae.editprofile.done', data);
                } else {
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__PROFILE_NOT_EDITED__'),
                        oae.api.i18n.translate('__MSG__PROFILE_DETAILS_EDIT_FAIL__', 'editprofile'),
                        'error');
                    // Enable the form and disable the submit button
                    $('#editprofile-form *', $rootel).prop('disabled', false);
                    $('#editprofile-form button[type="submit"]', $rootel).prop('disabled', true);
                }
            });

            // Avoid default form submit behavior
            return false;
        };

        /**
         * Resets the widget to its original state when the modal dialog is
         * first opened and when it is closed. Ideally this would only be
         * necessary when the modal is hidden, but IE10+ fires `input`
         * events while Bootstrap is rendering the modal, and those events can
         * "undo" parts of the reset. Hooking the `shown` event provides the
         * chance to compensate.
         */
        var setUpReset = function() {
            $('#editprofile-modal', $rootel).on('shown.bs.modal hidden.bs.modal', function (evt) {
                // Reset the form
                var $form = $('#editprofile-form', $rootel);
                $form[0].reset();
                oae.api.util.validation().clear($form);
                // Enable the form
                $('#editprofile-form *', $rootel).prop('disabled', false);
                $('#editprofile-form button[type="submit"]', $rootel).prop('disabled', true);
            });
        };

        /**
         * Initializes the edit profile modal dialog
         */
        var setUpEditProfileModal = function() {
            $(document).on('click', '.oae-trigger-editprofile', function() {
                $('#editprofile-modal', $rootel).modal({
                    'backdrop': 'static'
                });
                setUpEditProfile();
            });

            // Catch changes in the visibility radio group
            $rootel.on('change', '.oae-large-options-container input[type="radio"]', function() {
                $('.oae-large-options-container label', $rootel).removeClass('checked');
                $(this).parents('label').addClass('checked');
            });

            // Detect changes in the form and enable the submit button
            $('#editprofile-form', $rootel).on(oae.api.util.getFormChangeEventNames(), function() {
                $('#editprofile-form button[type="submit"]', $rootel).prop('disabled', false);
            });

            $('#editprofile-modal', $rootel).on('shown.bs.modal', function() {
                // Set focus to the display name field
                $('#editprofile-name', $rootel).focus();
            });
        };

        setUpReset();
        setUpEditProfileModal();

    };
});
