define(['jquery', 'oae.core', 'jquery.history'], function($, oae) {

    return function(uid, showSettings, widgetData) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * [getFolderContent description]
         *
         * @return {[type]} [description]
         */
        var getFolderContent = function() {
            $.ajax({
                'url': '/api/folder/' + widgetData.context.id + '/library?limit=8',
                'type': 'GET',
                'success': function(data) {
                    renderFolderProfile(data);
                }
            });
        };

        /**
         * [renderFolderProfile description]
         *
         * @return {[type]} [description]
         */
        var renderFolderProfile = function(content) {
            oae.api.util.template().render($('#folderprofile-template', $rootel), {
                'context': widgetData.context,
                'displayOptions': {},
                'items': content
            }, $('#folderprofile-container', $rootel));
        };

        getFolderContent();

    };
});
