casper.test.begin('Widget - Folder Shared', function(test) {

    /**
     * Open the folder shared modal with assertions
     */
    var verifyOpenFolderShared = function() {
        casper.waitForSelector('#folder-clip-container .oae-clip-content > button', function() {
            casper.click('#folder-clip-container .oae-clip-content > button');
            test.assertExists('.oae-trigger-foldershared', 'Folder shared trigger exists');
            casper.click('.oae-trigger-foldershared');
            casper.waitUntilVisible('#foldershared-modal', function() {
                test.assertVisible('#foldershared-modal', 'Folder shared pane is showing after trigger');
                casper.click('#folder-clip-container .oae-clip-content > button');
            });
        });
    };

    /**
     * Verify that all elements are present in the folder shared modal
     *
     * @param {User}    user1    The first test user profile object
     * @param {User}    user2    The second test user profile object
     */
    var verifyFolderSharedElements = function(user1, user2) {
        test.assertExists('#foldershared-modal .modal-header h3', 'Verify that the modal has a header');
        test.assertSelectorHasText('#foldershared-modal .modal-header h3', 'Shared with', 'Verify that the modal header reads \'Shared with\'');
        test.assertExists('#foldershared-modal .modal-body ul.oae-list li', 'Verify that the modal shows who the folder is shared with');
        test.assertExists('#foldershared-modal .modal-body ul.oae-list li .oae-thumbnail', 'Verify that the modal shows the pictures of the members');
        casper.waitForSelector('#foldershared-modal .modal-body ul.oae-list li[data-id="' + user1.id + '"]', function() {
            test.assertSelectorHasText('#foldershared-modal .modal-body ul.oae-list li[data-id="' + user1.id + '"] .oae-listitem-metadata h3', user1.displayName, 'Verify that the manager is shown in the members list');
            casper.waitForSelector('#foldershared-modal .modal-body ul.oae-list li[data-id="' + user2.id + '"]', function() {
                test.assertSelectorHasText('#foldershared-modal .modal-body ul.oae-list li[data-id="' + user2.id + '"] .oae-listitem-metadata h3', user2.displayName, 'Verify that the added member is shown in the members list');
                test.assertExists('#foldershared-modal .modal-body ul.oae-list li[data-id="' + user2.id + '"] .oae-listitem-metadata small', 'Verify that the modal shows the tenant of the members');
                test.assertSelectorHasText('#foldershared-modal .modal-body ul.oae-list li[data-id="' + user2.id + '"] .oae-listitem-metadata small', 'CasperJS Tenant', 'Verify that the metadata shows the correct tenant name');
                test.assertExists('#foldershared-modal .modal-body ul.oae-list li[data-id="' + user1.id + '"] a[href="' + user1.profilePath + '"][target="_blank"]', 'Verify that the members have a link to their profile that opens in a new page');
                test.assertExists('#foldershared-modal .modal-body ul.oae-list li[data-id="' + user2.id + '"] a[href="/me"][target="_blank"]', 'Verify that the the link to the current user\'s profile points to /me');
                // TODO: Check with actual infinite scrolling
                casper.waitForSelector('#foldershared-modal .modal-body ul.oae-list li:nth-child(9)', function() {
                    test.assertEvalEquals(function() {
                        return $('#foldershared-modal .modal-body ul.oae-list li').length;
                    }, 9, 'Verify that there are exactly 9 users this folder is shared with');
                });
            });
        });
    };

    casper.start(configUtil.tenantUI, function() {
        // Create a couple of users to test with
        userUtil.createUsers(9, function(user1, user2, user3, user4, user5, user6, user7, user8, user9) {
            // Login with the first user
            userUtil.doLogIn(user1.username, user1.password);

            folderUtil.createFolder(null, null, null, null, [user1.id, user2.id, user3.id, user4.id, user5.id, user6.id, user7.id, user8.id, user9.id], function(err, folderProfile) {
                // Log out of the first user
                userUtil.doLogOut();
                // Log in with the second user
                userUtil.doLogIn(user2.username, user2.password);
                // Open the folder profile
                uiUtil.openFolderProfile(folderProfile);

                casper.then(function() {
                    casper.echo('Verify open folder shared modal', 'INFO');
                    verifyOpenFolderShared();
                });

                casper.then(function() {
                    casper.echo('Verify folder shared elements', 'INFO');
                    verifyFolderSharedElements(user1, user2);
                });

                userUtil.doLogOut();
            });
        });
    });

    casper.run(function() {
        test.done();
    });
});
