define(['jquery', 'underscore', 'oae.core'], function($, _, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        // Caches the initialization data containing the context profile and the various strings
        var widgetData = null;

        var initValue = "";

        /**
         * Close the modal
         */
        var closeModal = function() {
            // Hide the modal
            $('#joingroup-modal', $rootel).modal('hide');

            // Reset 
            $('#joingroup-already-sent-request').hide();
            $('#joingroup-request-message').hide();
            $('#joingroup-already-sent-request').css("display", "none");
            $('#joingroup-request-message').css("display", "none");
        };

        /**
         * Create a request
         *
         * @param  {String}         role                
         * @param  {Function}       callback            Standard callback function
         * @param  {Object}         callback.err        Error object containing error code and error message
         */
        var createRequestJoinGroup = function(role) {
            
            oae.api.group.createRequestJoinGroup(widgetData.contextProfile.id, role, function(err, data) {
                if (err) {
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__SEND_REQUEST_GROUP_FAILED__', 'joingroup'),
                        oae.api.i18n.translate(err.msg, 'joingroup'),
                        'error');
                } else {
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__SEND_REQUEST_GROUP_SUCCESS__', 'joingroup'),
                        oae.api.i18n.translate('__MSG__SUCCESS_TO_SEND_REQUEST_JOIN_GROUP__', 'joingroup'));
                }

                closeModal();
                return false;
            });
        };
        
        /**
         * Remove a request
         *
         * @param  {String}         text                Text written by the user who wants to join the group
         * @param  {Function}       callback            Standard callback function
         * @param  {Object}         callback.err        Error object containing error code and error message
         */
        var removeRequestJoinGroup = function() {
            
            oae.api.group.removeRequestJoinGroup(widgetData.contextProfile.id, function(err, data) {
                if (err) {
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__REMOVE_REQUEST_GROUP_FAILED__', 'joingroup'),
                        oae.api.i18n.translate(err.msg, 'joingroup'),
                        'error');
                } else {
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__REMOVE_REQUEST_GROUP_SUCCESS__', 'joingroup'),
                        oae.api.i18n.translate('__MSG__SUCCESS_TO_REMOVE_REQUEST_JOIN_GROUP__', 'joingroup'));
                }

                closeModal();
                return false;
            });
        };

        /**
         * Update a request
         *
         * @param  {String}         text                Text written by the user who wants to join the group
         * @param  {Function}       callback            Standard callback function
         * @param  {Object}         callback.err        Error object containing error code and error message
         */
        var updateRequestJoinGroup = function(role) {
            
            oae.api.group.updateRequestJoinGroup(widgetData.contextProfile.id, role, function(err, data) {
                if (err) {
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__UPDATE_REQUEST_GROUP_FAILED__', 'joingroup'),
                        oae.api.i18n.translate(err.msg, 'joingroup'),
                        'error');
                } else {
                    oae.api.util.notification(
                        oae.api.i18n.translate('__MSG__UPDATE_REQUEST_GROUP_SUCCESS__', 'joingroup'),
                        oae.api.i18n.translate('__MSG__SUCCESS_TO_UPDATE_REQUEST_JOIN_GROUP__', 'joingroup'));
                }

                closeModal();
                return false;
            });
        };

        /**
         * Reset the widget to its original state when the modal dialog is closed
         */
        var setUpReset = function() {

            // Reset the form
            $('#joingroup-modal').on('hidden.bs.modal', function() {
                var $form = $('#joingroup-form', $rootel);
                $form[0].reset();
                oae.api.util.validation().clear($form);
            });
        };

        /**
         * Sets up 
         */
        var setUpJoinGroupModal = function() {

            // Show the modal
            $(document).on('oae.trigger.joingroup', function(ev, data, e) {

                widgetData = data;

                $('#joingroup-modal', $rootel).modal({
                    'backdrop': 'static'
                });

                // Get request  
                oae.api.group.getRequestJoinGroup(widgetData.contextProfile.id, function(err, request) {
                    if (request) {
                        renderAlert();
                        $('#joingroup-select-role').val(request.role);
                        initValue = request.role;
                    } else {
                        renderMessage();
                        $('#joingroup-select-role').val('none');
                        initValue = 'none';
                    }
                });

                // Set focus to the group name field
                $('#joingroup-modal', $rootel).on('shown.bs.modal', function () {
                    $('#joingroup-select-role', $rootel).focus();
                });

                // Set the save button to disabled
                $("#joingroup-save").prop('disabled', true);

            });
        };

        /**
         * Binds actions to various elements in the joingroup modal
         */
        var addBinding = function() {

            // Get the value on save and call the method joinGroup
            $rootel.on('click', '#joingroup-save', function() {
                var role = $("#joingroup-select-role").val();

                if (role === 'none') {
                    removeRequestJoinGroup();
                } else if (initValue === 'none') {
                    createRequestJoinGroup(role);
                } else {
                    updateRequestJoinGroup(role);
                }
            });

            // On changes, enable the save button
            $rootel.on('change', '#joingroup-select-role', function() {
                if ($("#joingroup-select-role").val() === initValue) {
                    $("#joingroup-save").prop('disabled', true);
                } else {
                    $("#joingroup-save").prop('disabled', false);
                }
            });
        };

        /**
         * Renders the alert message.
         */
        var renderAlert = function() {
            oae.api.util.template().render($('#joingroup-already-sent-request-template', $rootel), null , $('#joingroup-already-sent-request', $rootel));
            $('#joingroup-already-sent-request').show();
            $('#joingroup-already-sent-request').css("display", "block");
        };

        /**
         * Renders message.
         */
        var renderMessage = function() {
            oae.api.util.template().render($('#joingroup-request-message-template', $rootel), null , $('#joingroup-request-message', $rootel));
            $('#joingroup-request-message').show();
            $('#joingroup-request-message').css("display", "block");
        };

        setUpReset();
        setUpJoinGroupModal();
        addBinding();
    };
});
