define(['jquery', 'oae.core', 'http://www.google.com/jsapi'], function($, oae) {
    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * Resets the widget to its original state
         */
        var reset = function() {
            // Stop the video by removing it from the widget
            $('#landingvideo-video', $rootel).empty();
        };

        /**
         * Renders the video in the modal dialog
         */
        var renderLandingVideo = function() {
            oae.api.util.template().render($('#landingvideo-video-template', $rootel), null, $('#landingvideo-video', $rootel));
        };

        /**
         * Initialize the landing video modal dialog
         */
        var setUpLandingVideo = function() {
            $(document).on('click', '.oae-trigger-landingvideo', function() {
                $('#landingvideo-modal', $rootel).modal();
            });

            $('#landingvideo-modal', $rootel).on('hide', reset);
            $('#landingvideo-modal', $rootel).on('show', renderLandingVideo);
        };

        setUpLandingVideo();
    };
});
