// load the master sakai object to access all Sakai OAE API methods
require(['jquery', 'sakai/sakai.api.core'], function($, sakai) {

    /**
     * @name sakai_global.library
     *
     * @class library
     *
     * @description
     * Widget that lists all of the content items that live inside of a given
     * library
     *
     * @param {String} tuid Unique id of the widget
     * @param {Boolean} showSettings Show the settings of the widget or not
     */
    sakai_global.library = function(tuid, showSettings, widgetData, state) {

        /////////////////////////////
        // Configuration variables //
        /////////////////////////////

        var library = {  // global data for library widget
            sortBy: '_lastModified',
            sortOrder: 'desc',
            isOwnerViewing: false,
            default_search_text: '',
            userArray: [],
            contextId: false,
            infinityScroll: false,
            widgetShown: true,
            listStyle: 'list'
        };

        // DOM jQuery Objects
        var $rootel = $('#' + tuid);  // unique container for each widget instance
        var $library_check = $('.library_check', $rootel);
        var $library_items = $('#library_items', $rootel);
        var $library_check_all = $('#library_select_checkbox', $rootel);
        var $library_remove = $('#library_remove', $rootel);
        var $library_addto = $('#library_addpeople_button', $rootel);
        var $library_share = $('#library_content_share', $rootel);
        var $library_sortby = $('#library_sortby', $rootel);
        var $library_livefilter = $('#library_livefilter', $rootel);
        var $library_livefilter_container = $('#library_livefilter_container', $rootel);
        var $library_sortarea = $('#library_sortarea', $rootel);
        var $library_empty = $('#library_empty', $rootel);
        var $library_admin_actions = $('#library_admin_actions', $rootel);
        var library_remove_icon = '.library_remove_icon'
        var $library_search_button = $('#library_search_button', $rootel);
        var $library_result_count = $('.oae-search-result-count', $rootel);
        var $library_show_grid = $('.oae-listview-grid', $rootel);
        var $library_show_list = $('.oae-listview-list', $rootel);
        var $libraryAddContentOverlay = $('.sakai_add_content_overlay', $rootel);

        var currentGroup = false;

        ///////////////////////
        // Utility functions //
        ///////////////////////

        /**
         * Get personalized text for the given message bundle key based on
         * whether this library is owned by the viewer, or belongs to someone else.
         * The message should contain a '${firstname}' variable to replace with
         * and be located in this widget's properties files.
         *
         * @param {String} bundleKey The message bundle key
         */
        var getPersonalizedText = function(bundleKey) {
            if (currentGroup) {
                return sakai.api.i18n.getValueForKey(
                    bundleKey, 'library').replace(/\$\{firstname\}/gi,
                        currentGroup.properties['sakai:group-title']);
            } else if (library.isOwnerViewing) {
                return sakai.api.i18n.getValueForKey(
                    bundleKey, 'library').replace(/\$\{firstname\}/gi,
                        sakai.api.i18n.getValueForKey('YOUR').toLowerCase());
            } else {
                return sakai.api.i18n.getValueForKey(bundleKey, 'library').replace(/\$\{firstname\}/gi, sakai.api.User.getFirstName(sakai_global.profile.main.data) + '\'s');
            }
        };

        /**
         * Bring all of the topbar items (search, checkbox, etc.) back into its original state
         */
        var resetView = function() {
            $library_result_count.hide();
            $library_check_all.removeAttr('checked');
            $library_remove.attr('disabled', 'disabled');
            $library_addto.attr('disabled', 'disabled');
            $library_share.attr('disabled', 'disabled');
            $('#library_title_bar').show();
        };

        /**
         * Show or hide the controls on the top of the library (search box, sort, etc.)
         * @param {Object} show    True when you want to show the controls, false when
         *                         you want to hide the controls
         */
        var showHideTopControls = function(show) {
            if (show) {
                if (library.isOwnerViewing) {
                    $library_remove.show();
                    $library_admin_actions.show();
                    $libraryAddContentOverlay.show();
                }
                $('.oae-page-header-top-row', $rootel).show();
                $library_livefilter_container.show();
                $library_sortarea.show();
            } else {
                $('.oae-page-header-top-row', $rootel).hide();
                $library_admin_actions.hide();
                $library_livefilter_container.hide();
                $library_sortarea.hide();
            }
        };

        /**
         * Renders library title
         * @param {String} contextName The name to render
         * @param {Boolean} isGroup Flag if this is a groups library or not
         */
        var renderLibraryTitle = function(contextName, isGroup) {
            sakai.api.Util.TemplateRenderer('library_title_template', {
                isMe: library.isOwnerViewing,
                isGroup: isGroup,
                user: contextName
            }, $('#library_title_container', $rootel));
        };

        /////////////////////////////
        // Deal with empty library //
        /////////////////////////////

        /**
         * Function that manages how a library with no content items is handled. In this case,
         * the items container will be hidden and a 'no items' container will be shown depending
         * on what the current context of the library is
         */
        var handleEmptyLibrary = function() {
            resetView();
            $library_items.hide();
            var query = $library_livefilter.val();
            // We only show the controls when there is a search query.
            // All other scenarios with no items don't show the top controls
            if (!query) {
                showHideTopControls(false);
            } else {
                showHideTopControls(true);
            }
            // Determine the state of the current user in the current library
            var mode = 'user_me';
            if (sakai_global.profile && library.contextId !== sakai.data.me.userId) {
                mode = 'user_other';
            } else if (!sakai_global.profile && (library.isOwnerViewing || library.isMemberViewing)) {
                mode = 'group_manager_member';
            } else if (!sakai_global.profile) {
                mode = 'group';
            }
            $library_empty.html(sakai.api.Util.TemplateRenderer('library_empty_template', {
                mode: mode,
                query: query
            }));

            $library_empty.show();
        };

        ////////////////////
        // Load a library //
        ////////////////////

        /**
         * Load the items of the current library and start an infinite scroll on
         * them
         */
        var showLibraryContent = function() {
            resetView();
            var query = $library_livefilter.val();
            // Disable the previous infinite scroll
            if (library.infinityScroll) {
                library.infinityScroll.kill();
            }
            // Set up the infinite scroll for the list of items in the library
            var sortOrder = library.sortOrder;
            if (library.sortOrder === 'modified') {
                sortOrder = 'desc';
            }
            library.infinityScroll = $library_items.infinitescroll(sakai.config.URL.POOLED_CONTENT_SPECIFIC_USER.replace('__USERID__', library.contextId),
                {'startKey': function(item) {
                    return item.lastModified + ':' + item.contentId;
                }},  function(items, total) {
                if (total && query && query !== '*') {
                    $library_result_count.show();
                    var resultLabel = sakai.api.i18n.getValueForKey('RESULTS');
                    if (total === 1) {
                        resultLabel = sakai.api.i18n.getValueForKey('RESULT');
                    }
                    $library_result_count.children('.oae-search-result-count-label').text(resultLabel);
                    $library_result_count.children('.oae-search-result-count-count').text(total);
                }
                if (!sakai.data.me.anon) {
                    if (total !== 0) {
                        $('.oae-page-header-top-row', $rootel).show();
                        $('.oae-page-header-bottom-row', $rootel).show();
                    } else {
                        $('.oae-page-header-bottom-row', $rootel).hide();
                    }
                } else {
                    if (total !== 0) {
                        $('.oae-page-header-top-row', $rootel).show();
                    }
                }
                return sakai.api.Util.TemplateRenderer('library_items_template', {
                    'items': items,
                    'sakai': sakai,
                    'isMe': library.isOwnerViewing
                });
            }, handleEmptyLibrary, sakai.config.URL.INFINITE_LOADING_ICON, handleLibraryItems, function() {
                // Initialize content draggable
                sakai.api.Util.Draggable.setupDraggable({}, $library_items);
            }, sakai.api.Content.getNewList(library.contextId));
        };

        ////////////////////
        // State handling //
        ////////////////////

        /**
         * Deal with changed state in the library. This can be triggered when
         * a search was initiated, sort was changed, etc.
         */
        var handleHashChange = function(e) {
            var ls = $.bbq.getState('ls') || 'list';
            $('.oae-listview-options', $rootel).find('div').removeClass('selected');
            if (ls === 'list') {
                $('#library_items', $rootel).removeClass('oae-search-results-grid');
                $library_show_list.addClass('selected');
                $library_show_list.children().addClass('selected');
                library.listStyle = 'list';
            } else if (ls === 'grid') {
                $('#library_items', $rootel).addClass('oae-search-results-grid');
                $('.oae-listview-options', $rootel).find('div').removeClass('selected');
                $library_show_grid.addClass('selected');
                $library_show_grid.children().addClass('selected');
                library.listStyle = 'grid';
            }

            if (library.widgetShown) {
                // Set the sort states
                var parameters = $.bbq.getState();
                library.sortOrder = parameters.lso || 'modified';
                library.sortBy = parameters.lsb || '_lastModified';
                $library_livefilter.val(parameters.lq || '');
                $library_sortby.val(library.sortOrder);
                showLibraryContent();
            }
        };

        ////////////////////
        // Search library //
        ////////////////////

        /**
         * Search the current library. This function will extract the query
         * from the search box directly.
         */
        var doSearch = function() {
            var q = $.trim($library_livefilter.val());
            if (q) {
                $.bbq.pushState({ 'lq': q });
            } else {
                $.bbq.removeState('lq');
            }
        };

        var updateButtonData = function() {
            var shareIdArr = [];
            var addToIdArr = [];
            var addToTitleArr = [];
            $.each($('.library_check:checked:visible', $rootel), function(i, checked) {
                addToIdArr.push($(checked).attr('data-entityid'));
                addToTitleArr.push($(checked).attr('data-entityname'));
                shareIdArr.push($(checked).attr('data-entityid'));
                if (!$(checked).attr('data-canshare-error')) {
                    $(checked).attr('data-canshare-error', 'true');
                }
            });
            $library_share.attr('data-entityid', shareIdArr);
            $library_addto.attr('data-entityid', addToIdArr);
            $library_addto.attr('data-entityname', addToTitleArr);
            if (!shareIdArr.length) {
                $library_share.attr('disabled', 'disabled');
            }
        };

        ////////////////////
        // Event Handlers //
        ////////////////////

        var addBinding = function() {
            /**
             * Enable/disable the remove selected button depending on whether
             * any items in the library are checked
             */
            $rootel.on('change', '.library_check', function(ev) {
                if ($(this).is(':checked')) {
                    $library_remove.removeAttr('disabled');
                    $library_addto.removeAttr('disabled');
                    $library_share.removeAttr('disabled');
                } else if (!$('.library_check:checked', $rootel).length) {
                    $library_remove.attr('disabled', 'disabled');
                    $library_addto.attr('disabled', 'disabled');
                    $library_share.attr('disabled', 'disabled');
                }
                updateButtonData();
            });

            /**
             * Check/uncheck all loaded items in the library
             */
            $library_check_all.change(function(ev) {
                if ($(this).is(':checked')) {
                    $('.library_check').attr('checked', 'checked');
                    $library_remove.removeAttr('disabled');
                    $library_addto.removeAttr('disabled');
                    $library_share.removeAttr('disabled');
                } else {
                    $('.library_check').removeAttr('checked');
                    $library_remove.attr('disabled', 'disabled');
                    $library_addto.attr('disabled', 'disabled');
                    $library_share.attr('disabled', 'disabled');
                }
                updateButtonData();
            });

            /**
             * Gather all selected library items and initiate the
             * deletecontent widget
             */
            $library_remove.click(function(ev) {
                var $checked = $('.library_check:checked:visible', $rootel);
                if ($checked.length) {
                    var paths = [];
                    var collectionPaths = [];
                    $checked.each(function() {
                        paths.push($(this).attr('data-entityid'));
                        if ($checked.attr('data-type') === 'x-sakai/collection') {
                            collectionPaths.push($(this).attr('data-entityid'));
                        }
                    });
                    $(document).trigger('init.deletecontent.sakai', [{
                        paths: paths,
                        context: library.contextId
                    }, function(success) {
                        if (success) {
                            resetView();
                            $(window).trigger('lhnav.updateCount', ['library', -(paths.length)]);
                            library.infinityScroll.removeItems(paths);
                            if (collectionPaths.length) {
                                $(document).trigger('sakai.library.deletedCollections', {
                                    items: collectionPaths
                                });
                            }
                        }
                    }]);
                }
            });

            /**
             * Called when clicking the remove icon next to an individual content
             * item
             */
            $rootel.on('click', library_remove_icon, function(ev) {
                if ($(this).attr('data-entityid')) {
                    var paths = [];
                    var collection = false;
                    if ($(this).attr('data-type') === 'x-sakai/collection') {
                        collection = true;
                    }
                    paths.push($(this).attr('data-entityid'));
                    $(document).trigger('init.deletecontent.sakai', [{
                        paths: paths,
                        context: library.contextId
                    }, function(success) {
                        if (success) {
                            resetView();
                            $(window).trigger('lhnav.updateCount', ['library', -(paths.length)]);
                            if (collection) {
                                $(document).trigger('sakai.library.deletedCollections', {
                                    items: paths
                                });
                            }
                            library.infinityScroll.removeItems(paths);
                        }
                    }]);
                }
            });

            /**
             * Reload the library list based on the newly selected
             * sort option
             */
            $library_sortby.change(function(ev) {
                var query = $.trim($library_livefilter.val());
                var sortSelection = $(this).val();
                if (sortSelection === 'desc') {
                    library.sortOrder = 'desc';
                    library.sortBy = 'filename';
                } else if (sortSelection === 'asc') {
                    library.sortOrder = 'asc';
                    library.sortBy = 'filename';
                } else {
                    library.sortOrder = 'modified';
                    library.sortBy = '_lastModified';
                }
                $.bbq.pushState({'lsb': library.sortBy, 'lso': library.sortOrder, 'lq': query});
            });

            /**
             * Initiate a library search when the enter key is pressed
             */
            $library_livefilter.keyup(function(ev) {
                if (ev.keyCode === 13) {
                    doSearch();
                }
                return false;
            });

            /**
             * Initiate a search when the search button next to the search
             * field is pressed
             */
            $library_search_button.click(doSearch);

            /**
             * An event to listen from the worldsettings dialog so that we can refresh the title if it's been changed.
             * @param {String} title     New group name
             */
            $(window).on('updatedTitle.worldsettings.sakai', function(e, title) {
                renderLibraryTitle(title, true);
            });

            /**
             * Listen for newly the newly added content or newly saved content
             * @param {Object} data        Object that contains the new library items
             * @param {Object} library     Context id of the library the content has been added to
             */
            $(document).on('done.addcontent.sakai', function(e, data, library) {
                if (library === library.contextId || library.contextId === sakai.data.me.userId) {
                    library.infinityScroll.prependItems(data);
                }
            });

            /**
             * Keep track as to whether the current library widget is visible or not. If the
             * widget is not visible, it's not necessary to respond to hash change events.
             */
            $(window).on(tuid + '.shown.sakai', function(e, shown) {
                library.widgetShown = shown;
            });
            /**
             * Bind to hash changes in the URL
             */
            $(window).on('hashchange', handleHashChange);

            $library_show_list.click(function() {
                $.bbq.pushState({'ls': 'list'});
            });

            $library_show_grid.click(function() {
                $.bbq.pushState({'ls': 'grid'});
            });
        };

        ////////////////////////////////////////////
        // Data retrieval and rendering functions //
        ////////////////////////////////////////////

        /**
         * Process library item results from the server
         */
        var handleLibraryItems = function(results, callback) {
            sakai.api.Content.prepareContentForRender(results, sakai.data.me, function(contentResults) {
                if (contentResults.length > 0) {
                    callback(contentResults);
                    showHideTopControls(true);
                    $library_empty.hide();
                    $library_items.show();
                } else {
                    callback([]);
                }
            });
        };

        /////////////////////////////
        // Initialization function //
        /////////////////////////////

        var initGroupLibrary = function() {
            sakai.api.Server.loadJSON('/system/userManager/group/' +  library.contextId + '.json', function(success, data) {
                if (success) {
                    currentGroup = data;
                    var contextName = sakai.api.Util.Security.safeOutput(currentGroup.properties['sakai:group-title']);
                    library.isOwnerViewing = sakai.api.Groups.isCurrentUserAManager(currentGroup.properties['sakai:group-id'], sakai.data.me, currentGroup.properties);
                    library.isMemberViewing = sakai.api.Groups.isCurrentUserAMember(currentGroup.properties['sakai:group-id'], sakai.data.me);
                    finishInit(contextName, true);
                }
            });
        };

        var initUserLibrary = function() {
            library.contextId = sakai_global.profile.main.data.userId;
            var contextName = sakai.api.User.getFirstName(sakai_global.profile.main.data);
            if (library.contextId === sakai.data.me.userId) {
                library.isOwnerViewing = true;
            }
            finishInit(contextName, false);
        };

        /**
         * Initialization function that is run when the widget is loaded. Determines
         * which mode the widget is in (settings or main), loads the necessary data
         * and shows the correct view.
         */
        var doInit = function() {
            library.contextId = '';

            // We embed the deletecontent widget, so make sure it's loaded
            sakai.api.Widgets.widgetLoader.insertWidgets(tuid, false);

            if (widgetData && widgetData.library) {
                library.contextId = widgetData.library.groupid;
                initGroupLibrary();
            } else if (sakai_global.group && sakai_global.group.groupId) {
                library.contextId = sakai_global.group.groupId;
                initGroupLibrary();
            } else {
                initUserLibrary();
            }
        };

        /**
         * Additional set-up of the widget
         */
        var finishInit = function(contextName, isGroup) {
            if (library.contextId) {
                library.default_search_text = getPersonalizedText('SEARCH_YOUR_LIBRARY');
                library.currentPagenum = 1;
                library.listStyle = $.bbq.getState('ls') || 'list';
                handleHashChange(null, true);
                renderLibraryTitle(contextName, isGroup);
            } else {
                debug.warn('No user found for My Library');
            }
            addBinding();
        };

        // run the initialization function when the widget object loads
        doInit();

    };

    // inform Sakai OAE that this widget has loaded and is ready to run
    sakai.api.Widgets.widgetLoader.informOnLoad('library');
});