define(['jquery', 'oae/api/oae.core'], function($, oae) {

    return function(uid, showSettings, widgetData) {

        var $rootel = $('#' + uid);

        var infinityScroll = false;

        /**
         * Render the default template when no results are found. This function will
         * be called by the infinite scroll plugin
         */
        var handleEmptyResultList = function() {
            oae.api.util.renderTemplate($('#library_noresults_template'), null, $('.oae-list', $rootel));
        };

        var getLibrary = function() {
            // Disable the previous infinite scroll
            if (infinityScroll) {
                infinityScroll.kill();
            }

            // Set up the infinite scroll for the list of search results
            infinityScroll = $('.oae-list', $rootel).infiniteScroll('/api/content/library/' + widgetData.principalId, {
                'limit': 12
            }, '#library_template', {
                'emptyListProcessor': handleEmptyResultList,
                'postRenderer': function(data) {
                    $('#library_numberFound').text(data.total);
                }
            });
        };

        var doInit = function() {
            getLibrary();
        };

        doInit();
    };
});
