casper.test.begin('Widget - Link preview', function(test) {

    /**
     * Verifies that the link is shown in an iframe on the content profile
     */
    var verifyLinkPreview = function() {
        casper.waitForSelector('#linkpreview-container', function() {
            test.assertExists('#linkpreview-container', 'The link preview container is present');
            test.assertExists('#linkpreview-container iframe', 'The link preview iframe is present');
        });
    };

    casper.start(configUtil().tenantUI, function() {
        // Create a couple of users to test with
        var user1 = null;
        var user2 = null;
        userUtil().createUsers(2, function(users) {
            user1 = users[0];
            user2 = users[1];
        });

        // Log in with that user
        casper.then(function() {
            userUtil().doLogIn(user1.username, configUtil().defaultUserPassword);
        });

        // Verify default previews
        casper.then(function() {
            casper.echo('Verify link preview elements present', 'INFO');
            contentUtil().createLink(null, null, null, function(link) {
                casper.thenOpen(configUtil().tenantUI + link.profilePath);

                casper.then(function() {
                    verifyLinkPreview();
                });
            });
        });

        // Log out at the end of the test
        casper.then(function() {
            userUtil().doLogOut();
        });
    });

    casper.run(function() {
        test.done();
    });
});
