casper.test.comment('Widget - Members');

/**
 * Verify that the members can be searched
 */
var verifyMembersSearch = function(user) {
    // Search for 'adfasdf'
    casper.fill('form.form-search', {
        'search-query': 'adfasdf'
    }, false);
    casper.click('form.form-search button[type="submit"]');
    // Verify that no results come back and a message is shown
    casper.waitForSelector('.oae-list .alert-info', function() {
        casper.test.assertExists('.oae-list .alert-info', 'No results are returned for \'adfasdf\' and a message is shown')
        // Search for 'baloons' which should return 1 result
        casper.fill('form.form-search', {
            'search-query': user.displayName
        }, false);
        casper.click('form.form-search button[type="submit"]');
        // Verify one search result comes back
        casper.waitForSelector('.oae-list li', function() {
            casper.test.assertExists('.oae-list li', '1 result is returned for \'' + user.displayName + '\'');
            casper.test.assertSelectorHasText('.oae-list li .oae-tile-metadata a', user.displayName, 'The returned user has the name \'' + user.displayName + '\'');
            // Reset the form
            casper.fill('form.form-search', {
                'search-query': ''
            }, false);
            casper.click('form.form-search button[type="submit"]');
        });
    });
};

/**
 * Verify that the view mode can be changed through the list options
 */
var verifyMembersViewMode = function() {
    // Toggle the list options
    casper.click('.oae-list-options-toggle');
    // Verify compact list
    casper.click('#members-widget .oae-list-options button[data-type="oae-list-compact"]');
    casper.test.assertExists('.oae-list.oae-list-compact', 'Members can be switched to compact view');
    // Verify details list
    casper.click('#members-widget .oae-list-options button[data-type="oae-list-details"]');
    casper.test.assertExists('.oae-list.oae-list-details', 'Members can be switched to details view');
    // Switch back to grid view
    casper.click('#members-widget .oae-list-options button[data-type="oae-list-grid"]');
    casper.test.assertExists('.oae-list.oae-list-grid', 'Members can be switched to grid view');
};

/**
 * Verify if all elements are present in the members widget
 */
var verifyMembersElements = function() {
    // Verify there is a dummy list item with action buttons
    casper.test.assertExists('#members-widget .oae-list li:first-child', 'Initial dummy list item is present');
    // Verify dummy list item has add members button
    casper.test.assertExists('#members-widget .oae-list li:first-child .group-trigger-manageaccess-add', 'The first list item has a \'Add members\' trigger');
    // Verify add members button triggers manageaccess widget
    casper.click('#members-widget .oae-list li:first-child .group-trigger-manageaccess-add');
    casper.waitForSelector('#manageaccess-modal', function() {
        casper.test.assertExists('#manageaccess-modal #manageaccess-modal-title', 'The manageaccess widget can be triggered to add members');
        casper.click('#manageaccess-modal .close');
    });
    // Verify dummy list item has add members button
    casper.test.assertExists('#members-widget .oae-list li:first-child .group-trigger-manageaccess', 'The first list item has a \'Manage access\' trigger');
    // Verify add members button triggers manageaccess widget
    casper.click('#members-widget .oae-list li:first-child .group-trigger-manageaccess');
    casper.waitForSelector('#manageaccess-modal', function() {
        casper.test.assertExists('#manageaccess-modal #manageaccess-modal-title', 'The manageaccess widget can be triggered to manage access');
        casper.click('#manageaccess-modal .close');
    });
    // Verify list options are there
    casper.test.assertExists('#members-widget .oae-list-options', 'The list options are present');
    // Verify list options contain a switch view button
    casper.test.assertExists('#members-widget .oae-list-options button[data-type="oae-list-compact"]', 'The \'Compact\' list view button is present');
    casper.test.assertExists('#members-widget .oae-list-options button[data-type="oae-list-details"]', 'The \'Details\' list view button is present');
    casper.test.assertExists('#members-widget .oae-list-options button[data-type="oae-list-grid"]', 'The \'Grid\' list view button is present');
    // Verify list options contain a search field
    casper.test.assertExists('#members-widget .oae-list-options #members-search-query', 'The search box is present');
};

casper.start('http://test.oae.com', function() {
    // Create a couple of users to test with
    var user1 = null;
    userUtil().createUsers(1, function(users) {
        user1 = users[0];
    });

    // Login with that user
    casper.then(function() {
        userUtil().doLogIn(user1.username, 'password');
    });

    casper.then(function() {
        groupUtil().createGroup(function(group) {
            casper.thenOpen('http://test.oae.com' + group.profilePath + '/members', function() {
                casper.then(function() {
                    casper.echo('Verify members elements present', 'INFO');
                    casper.waitForSelector('div[data-id="members"]', verifyMembersElements);
                });

                casper.then(function() {
                    casper.echo('Verify members view modes', 'INFO');
                    verifyMembersViewMode();
                });

                casper.then(function() {
                    casper.echo('Verify members search', 'INFO');
                    verifyMembersSearch(user1);
                });
            });
        });
    });

    // Log out at the end of the test
    casper.then(function() {
        userUtil().doLogOut();
    });
});

casper.run(function() {
    this.test.done();
});
