define(['jquery', 'oae.core'], function($, oae) {

    return function(uid, showSettings, widgetData) {

        var $rootel = $('#' + uid);

        // Variable that will be used to keep track of the current infinite scroll instance
        var infinityScroll = false;

        /**
         * Show the empty list message when no results are found. This function will
         * be called by the infinite scroll plugin.
         */
        var handleEmptyResultList = function() {
            oae.api.util.template().render($('#memberships-noresults-template'), null, $('.oae-list', $rootel));
        };

        /**
         * Initialize a new infinite scroll container that fetches a user's memberships. 
         * This will detect when a search is happening and will change the endpoint 
         * accordingly.
         */
        var getMemberships = function() {
            // Disable the previous infinite scroll
            if (infinityScroll) {
                infinityScroll.kill();
            }

            // Detect whether or not we need to do a search by checking if
            // the History.js state has a query parameter
            var query = History.getState().data.query;
            $('.search-query', $rootel).val(query);

            var url = '/api/user/' + widgetData.principalId + '/memberships';
            if (query) {
                url = '/api/search/memberships/' + widgetData.principalId;
            }

            // Set up the infinite scroll instance
            infinityScroll = $('.oae-list', $rootel).infiniteScroll(url, {
                'limit': 12,
                'q': query
            }, '#memberships-template', {
                'postProcessor': function(data) {
                    // Let the template know whether or not the current list
                    // is a main list or a search list, as different paging
                    // keys need to be provided for each
                    data.isSearch = query ? true : false;
                    return data;
                },
                'emptyListProcessor': handleEmptyResultList
            });
        };

        /**
         * If the current user is an anonymous user, we disable the toggle that displays
         * the different list item actions.
         */
        var setUpAnon = function() {
            if (oae.data.me.anon) {
                $('.oae-list-options-toggle', $rootel).attr('disabled', 'disabled');
            }
        };

        /**
         * Initiate a search with the search query entered by the user. An empty
         * search will just show the normal list.
         */
        var search = function() {
            var query = $('.search-query', $(this)).val();
            // Push the new query to a new History.js state. We make sure to take the
            // existing state data parameters with us and construct a new URL based on
            // the existing base URL, allowing for page refreshing and bookmarking.
            var newState = $.extend({}, History.getState().data, {
                'query': query
            });
            History.pushState(newState, null, $.url().attr('path') + '?q=' + oae.api.util.security().encodeForURL(query));
            // Avoid that the search form is being submitted
            return false;
        };

        /**
         * Extract the search query from the current URL and load the initial memberships list.
         * This will only be executed when the page is loaded.
         */
        var initMemberships = function() {
            // We parse the URL fragment that's inside of the current History.js hash.
            // We expect to find the search query in the `?q=<query>` part of the URL.
            var initialState = $.url(History.getState().hash);
            var query = initialState.param().q;
            // Include the query into the History.js state. This requires extending the existing
            // state as to not to lose any of the current state information (page, etc.).
            var newState = $.extend({}, History.getState().data, {
                'query': query,
                '_': Math.random()
            });

            // Replace the current History.js state to have the query information. This
            // is necessary because a newly loaded page will not contain the data object in its
            // state. Calling the replaceState function will automatically trigger the statechange
            // event, which will take care of the membership listing. The random number is added to 
            // the data object to make sure that the statechange event is triggered after a page reload.
            History.replaceState(newState);
        };

        /**
         * Add the different event bindings
         */
        var addBinding = function() {
            // Set up search event
            $rootel.on('submit', '#memberships-search-form', search);
            // Listen to History.js state changes
            $(window).on('statechange', function() {
                // Only re-load the memberships list when the widget is currently visible
                if ($rootel.is(':visible')) {
                    getMemberships();
                }
            });
        };

        addBinding();
        setUpAnon();
        initMemberships();

    };
});
