define(
    [
        'jquery', 'oae.core',
        'jquery.timeago'
    ],
    function ($, oae, timeago) {

        return function (uid, showSettings, data) {

            // Properties
            var $rootel = $('#' + uid);

            var initChildren = function() {

                console.log('[mobileContentDetail] initChildren');

                if(data.profile.hasOwnProperty('id') && data.profile.resourceType == "content"){
                    oae.api.content.getComments(data.profile.id, 0, 5, function(e, data){
                        console.log(data);
                    });
                }

                if(data.profile && data.profile != null){
                    oae.api.util.template().render('#mobile-detail-template', data, $('#mobile-detail-container', $rootel));
                }else{
                    var error = {};
                    oae.api.util.template().render('#mobile-detail-error-template', error, $('#mobile-detail-error-container', $rootel));
                }
            };

            /**
             * Initialize this mobileactivity widget
             */
            var init = function () {
                initChildren();
            };

            init();
        };
    }
);