define(
    [
        'jquery', 'underscore', 'oae.core'
    ],
    function ($, _, oae) {

        return function (uid, show, _data, callback) {

            // Properties
            var $rootel = $('#' + uid);
            var constants = _data.constants;
            var settings = _data.settings;

            /**
             * Build widget children
             */
            var initChildren = function() {
                var data = {'currentUser': oae.data.me.displayName, 'settings': settings};
                oae.api.util.template().render('#mobile-navigation-template',
                    data, $('#mobile-navigation-container', $rootel));
            };

            /**
             * Bind events to components
             */
            var addBinding = function() {
                $('li').bind('click', onNavigationItemClick);
            };

            /**
             * When a navigation item gets clicked
             * @param {Event}   e                       The dispatched event
             * @param {Object}  e.currentTarget         The item that is clicked on
             */
            var onNavigationItemClick = function(e) {
                var action = $(e.currentTarget).attr('data-action');
                $(document).trigger(constants.events.activities.menuclicked, action);
            };

            /**
             * Initialize widget
             */
            var init = function() {
                // Init children
                initChildren();
                // Add binding
                addBinding();
            };

            init();
        };
    }
);
