define(['jquery', 'oae/api/oae.core'], function($, oae) {

    return function(uid, showSettings) {

        var $rootel = $('#' + uid);

        /**
         * Adds the specified widget to the page
         * TODO: Create better way of constructing widgetData to pass to widget
         * TODO: Better management of widget containers
         *
         * @param  {Object}   data   The data for the widget to load
         */
        var initWidget = function(hash, widgets) {
            $('#oae-left-column', $rootel).find('ul li').removeClass('selected');
            $('#oae-left-column ul li[data-l="' + hash + '"]', $rootel).addClass('selected');

            // Empty the widget container
            // TODO: Better handling of widget containers with caching
            $('#page-widgetcontainer').empty();

            // Insert the new widget
            oae.api.widget.insertWidget(widgets, null, $('#page-widgetcontainer'), null, {
                'principalId': oae.data.me.userId,
                'canManage': false
            }, null);
        };

        /**
         * Determines which page to load based on the event that's passed in and passes the data on to `initWidget`
         *
         * @param  {Object}   ev   Event retrieved after an item in the left hand navigation is clicked
         */
        var loadPage = function(ev) {
            var widgetsToLoad = $(ev.target).attr('data-widgets');
            initWidget($(ev.target).parent().attr('data-l'), widgetsToLoad);
        };

        /**
         * Adds binding to various elements in the widget
         */
        var addBinding = function() {
            $rootel.on('click', '.lhnavigation-link', loadPage);
        };

        /**
         * Initializes the widget and selects the page that is defined in the hash or default first page
         */
        var doInit = function() {
            // Add binding to the navigation
            addBinding();

            // Render the navigation
            oae.api.util.renderTemplate('#lhnavigation-firstcolumn-template', null, $('#oae-left-column'));

            var hash = 'dashboard';
            if ($.bbq.getState('l')) {
                hash = $.bbq.getState('l');
            }
            var data = $('#oae-left-column ul li[data-l="' + hash + '"] a', $rootel).attr('data-widgets')
            initWidget(hash, data);
        };

        doInit();
    };
});
