define(['jquery', 'oae/api/oae.core'], function($, oae) {

    return function(uid, showSettings) {

        var $rootel = $('#' + uid);

        var renderMembers = function(err, members) {
            if (err) {
                throw new Error('The participants could not be retrieved');
            }

            oae.api.util.renderTemplate('#participants_list_template', {
                'participants': members.results
            }, $('.oae-list', $rootel));
        };

        var getMembers = function(page) {
            oae.api.content.getMembers('c:camtest:VVJo1fC4zif', page, null, renderMembers)
        };

        var addBinding = function() {
            $rootel.on('click', '.oae-list-item-right', function() {
                $(this).parent().toggleClass('active');
            });

            $('#participants_switch_view').on('click', function() {
                $rootel.find('.oae-list').toggleClass('grid');
            });
        };

        var doInit = function() {
            addBinding();
            getMembers();
            $('.participants_widget').show();
        };

        doInit();
    };
});
