define(['jquery', 'oae.core'], function($, oae) {

    return function(uid) {

        // The widget container
        var $rootel = $('#' + uid);

        /**
         * Resets the widget to its original state
         */
        var reset = function() {
            // Stop the video by removing it from the widget
            $('#productvideo-modal', $rootel).empty();
        };

        /**
         * Renders the video in the modal dialog
         */
        var renderProductVideo = function() {
            oae.api.util.template().render($('#productvideo-template', $rootel), null, $('#productvideo-modal', $rootel));
        };

        /**
         * Initialize the landing video modal dialog
         */
        var setUpProductVideo = function() {
            $(document).on('click', '.oae-trigger-productvideo', function() {
                // Don't add a static backdrop for this modal, as it won't have a cancel
                // or close button, so it still needs to be possible to close the modal
                // by clicking outside of it
                $('#productvideo-modal', $rootel).modal();
            });

            $('#productvideo-modal', $rootel).on('hide', reset);
            $('#productvideo-modal', $rootel).on('show', renderProductVideo);
        };

        setUpProductVideo();
    };
});
