define(['jquery', 'oae.core', 'jquery.carouFredSel'], function($, oae) {

    return function(uid, showSettings) {

        /**
         * Initializes the carousel and binds the next and previous buttons
         */
        var initCarousel = function() {
            // A fix was applied to bootstrap dropdown binding for the carousel to function properly
            // https://github.com/twitter/bootstrap/issues/6862
            $("#relatedcontent-carousel").carouFredSel({
                auto: false,
                width: '100%',
                scroll: 2,
                infinite: true,
                next: "#relatedcontent-carousel-next",
                prev: "#relatedcontent-carousel-prev"
            });
        };

        /**
         * Renders the HTML for the carousel
         * @param  {Object}   data   The data returning from the relatedcontent feed
         */
        var renderCarousel = function(data) {
            oae.api.util.template().render('#relatedcontent-template', {'data': data}, $('#relatedcontent-carousel-container'));
            initCarousel();
        };

        /**
         * Gets the first 20 items of related content
         * @param  {Object}   ctx   The context object passed in from the content profile containing more information on the content that is viewed
         */
        var getRelatedContent = function(ctx) {
            $.ajax({
                'url': '/api/search/relatedcontent/' + ctx.id + '?limit=20',
                'success': function(data) {
                    renderCarousel(data);
                }
            });
        };

        /**
         * Binds to the context event of the content profile page. The context is needed to determine which content
         * item to get related content for. When the context is retrieved the related content will be loaded.
         */
        var initGetContext = function() {
            // Trigger and bind an event that will ask for the context of the widget
            $(document).on('oae.context.send', function(ev, ctx) {
                getRelatedContent(ctx);
            });
            $(document).trigger('oae.context.get');
        };

        initGetContext();

    };
});
