casper.test.comment('Widget - Revisions');

/**
 * Open the revisions modal with assertions
 */
var verifyOpenRevisionsModal = function() {
    casper.waitForSelector('#content-clip-container .oae-clip-content > button', function() {
        casper.click('#content-clip-container .oae-clip-content > button');
        casper.test.assertExists('.oae-trigger-revisions', 'Revisions trigger exists');
        casper.click('.oae-trigger-revisions');
        casper.wait(1000, function() {
            casper.test.assertVisible('#revisions-modal', 'Revisions pane is showing after trigger');
            casper.click('#content-clip-container .oae-clip-content > button');
        });
    });
};

/**
 * Open the revisions modal
 */
var openRevisionsModal = function(callback) {
    casper.waitForSelector('#content-clip-container .oae-clip-content > button', function() {
        casper.click('#content-clip-container .oae-clip-content > button');
        casper.click('.oae-trigger-revisions');
        casper.wait(1000, function() {
            casper.click('#content-clip-container .oae-clip-content > button');
            if (callback) {
                callback();
            }
        });
    });
};

/**
 * Verify that all revisions elements are present for content items
 */
var verifyContentRevisionsElements = function() {
    // Verify the list of revisions is present
    casper.test.assertExists('ul#revisions-list', 'The list of content revisions is present');
    // Verify that there is at least one item in the revisions list
    casper.test.assertExists('ul#revisions-list li', 'There is at least one content revision present');
    // Verify current revision cannot be restored
    casper.test.assertDoesntExist('ul#revisions-list li:first-child .revisions-list-actions-restore', 'The current content revision cannot be restored');
    // Verify other revisions can be restored
    casper.test.assertExists('ul#revisions-list li:first-child + li .revisions-list-actions-restore', 'All other content revisions can be restored');
    // Verify that the revision can be downloaded
    casper.test.assertExists('ul#revisions-list li .revisions-list-actions-download', 'The download button for each content revision is present');
    // Verify preview for revision is shown
    casper.test.assertExists('#revisions-image-container', 'The content revision preview container is present');
    casper.test.assertExists('#revisions-image-container img', 'The content revision preview image is present');
};

/**
 * Verify that a content revision can be restored
 */
var verifyRestoringContentRevision = function() {
    // Verify restoring a revision
    casper.click('ul#revisions-list li:first-child + li .revisions-list-actions-restore');
    // Wait for the success notification
    casper.waitForSelector('#oae-notification-container .alert', function() {
        casper.test.assertDoesntExist('#oae-notification-container .alert.alert-error', 'The content revision can be restored');
        casper.click('#oae-notification-container .close');
    });
};

/**
 * Verify that all revisions elements are present for collaborative documents
 */
var verifyCollabDocRevisionsElements = function() {
    // Verify the list of revisions is present
    casper.test.assertExists('ul#revisions-list', 'The list of collabdoc revisions is present');
    // Verify that there is at least one item in the revisions list
    casper.test.assertExists('ul#revisions-list li', 'There is at least one collabdoc revision present');
    // Verify current revision can be restored
    casper.test.assertExists('ul#revisions-list li:first-child .revisions-list-actions-restore', 'The current collabdoc revision can be restored');
    // Verify preview for revision is shown
    casper.test.assertExists('#revisions-etherpad-container', 'The collabdoc revision preview is present');
};

/**
 * Verify that a content revision can be restored
 */
var verifyRestoringCollabDocRevision = function() {
    // Verify restoring a revision
    casper.click('ul#revisions-list li:first-child .revisions-list-actions-restore');
    // Wait for the success notification
    casper.waitForSelector('#oae-notification-container .alert', function() {
        casper.test.assertDoesntExist('#oae-notification-container .alert.alert-error', 'The collabdoc revision can be restored');
        casper.click('#oae-notification-container .close');
    });
};

casper.start('http://test.oae.com', function() {
    // Create a couple of users to test with
    var user1 = null;
    userUtil().createUsers(1, function(users) {
        user1 = users[0];
    });

    // Login with that user
    casper.then(function() {
        userUtil().doLogIn(user1.username, 'password');
    });

    // Create a content item, go to the content profile page and verify opening the revisions dialog
    casper.then(function() {
        casper.echo('Verify revisions modal', 'INFO');
        contentUtil().createFile(null, function(contentURL) {
            casper.thenOpen('http://test.oae.com' + contentURL, function() {
                contentUtil().createRevision(function() {
                    verifyOpenRevisionsModal();
                });
            });
        });
    });

    casper.then(function() {
        casper.echo('Verify content revisions elements', 'INFO');
        verifyContentRevisionsElements();
    });

    casper.then(function() {
        casper.echo('Verify restoring content revision', 'INFO');
        verifyRestoringContentRevision();
    });

    // Create a collaborative document, go to the content profile page and verify the revision elements are present
    casper.then(function() {
        casper.echo('Verify collabdoc revisions elements', 'INFO');
        collabDocUtil().createCollabDoc(function(collabdoc) {
            casper.thenOpen('http://test.oae.com' + collabdoc.profilePath, function() {
                openRevisionsModal(verifyCollabDocRevisionsElements);
            });
        });
    });

    casper.then(function() {
        casper.echo('Verify restoring collabdoc revision', 'INFO');
        verifyRestoringCollabDocRevision();
    });

    // Log out at the end of the test
    casper.then(function() {
        userUtil().doLogOut();
    });
});

casper.run(function() {
    this.test.done();
});
