define(['jquery', 'oae/api/oae.core'], function($, oae) {

    return function(uid) {

        var $rootel = $('#' + uid);

        var infinityScroll = false;

        /**
         * Render the default template when no results are found. This function will
         * be called by the infinite scroll plugin
         */
        var handleEmptyResultList = function() {
            oae.api.util.renderTemplate($('#searchall_noresults_template'), null, $('#searchall_results_container'));
        };

        /**
         * Kick off a search with a specific query. This function will
         * initiate an infinite scroll for each search
         */
        var search = function() {
            // Disable the previous infinite scroll
            if (infinityScroll) {
                infinityScroll.kill();
            }

            // Set up the infinite scroll for the list of search results
            infinityScroll = $('#searchall_results_container').infiniteScroll('/api/search/general/all', {
                'limit': 12,
                'q': $.bbq.getState('q')
            }, '#search_general_results_template', {
                'emptyListProcessor': handleEmptyResultList,
                'postRenderer': function(data) {
                    $('#searchall_numberFound').text(data.total);
                }
            });
        };

        /////////////////////////
        // Initialise Function //
        /////////////////////////

        $(window).on('hashchange', function(ev) {
            if ($.bbq.getState('l') === 'all') {
                search();
            }
        });

        search();
    };
});
