casper.test.comment('Widget - Share');

/**
 * Verify that the share popover can be opened in the content library
 */
var verifyOpenLibraryShare = function() {
    casper.waitForSelector('li.oae-list-actions + li .oae-list-grid-item input[type="checkbox"]', function() {
        casper.click('li.oae-list-actions + li .oae-list-grid-item input[type="checkbox"]');
        casper.test.assertExists('#contentlibrary-widget .oae-list-options button.oae-trigger-share:not([disabled])', 'The share button is enabled');
        casper.click('#contentlibrary-widget .oae-list-options button.oae-trigger-share');
        casper.waitForSelector('#share-container', function() {
            casper.test.assertExists('#share-container', 'The share popover opens after trigger');
        });
    });
};

/**
 * Verifies that the share form elements are present
 */
var verifyLibraryShareElements = function() {
    casper.test.assertExists('form#share-form', 'The share form is present');
    casper.test.assertExists('form#share-form ul.as-selections', 'The share form autosuggest is present');
    casper.test.assertExists('form#share-form button#share-save', 'The share form submit button is present');
    casper.test.assertExists('form#share-form button#share-save:disabled', 'The share form submit button is disabled by default');
    casper.test.assertExists('form#share-form button#share-cancel', 'The share cancel button is present');
};

/**
 * Verify that a content item can be shared with another user
 *
 * @param  {User}    user    The user object returned from the server on creation
 */
var verifySharing = function(user) {
    // Share it with the second user that was created for the test
    casper.thenEvaluate(function(user) {
        document.querySelector('#share-form .as-selections input').value = user.displayName;
    }, user);
    // Click the input field to trigger the list
    casper.click('#share-form .as-selections input');
    // Wait for the list to render suggestions
    casper.waitForSelector('.as-list li', function() {
        // Verify there is at least one item in the autosuggestions
        casper.test.assertExists('.as-list li', 'At least one suggestion for \'' + user.displayName + '\' was returned from the server');
        // Click the first suggestion in the list
        casper.click('.as-list li');
        // Check that the share button is now enabled
        casper.test.assertExists('form#share-form button[type="submit"]:not([disabled])', 'The share form submit button is enabled after autosuggest selection');
        // TODO: The casper events aren't properly sent out.
        //       This causes the share button to remain disabled.
        //       We're removing this state manually for now so we can continue the test.
        casper.evaluate(function() {
            document.getElementById('share-save').removeAttribute('disabled');
        });
        // Submit the form
        casper.click('form#share-form button[type="submit"]');
        // Wait for the success notification
        casper.waitForSelector('#oae-notification-container .alert', function() {
            casper.test.assertDoesntExist('#oae-notification-container .alert.alert-error', 'The content item was successfully shared');
            casper.click('#oae-notification-container .close');
            // Log out
            casper.then(function() {
                userUtil().doLogOut();
            });
            // Log in with user I shared with
            casper.then(function() {
                userUtil().doLogIn(user.username, 'password');
            });
            // Navigate to the content library
            casper.thenOpen('http://test.oae.com/me/library', function() {

                // Wait till the content library has loaded by waiting till the second item is there
                // The first item contains the upload / new doc actions
                casper.waitForSelector('#contentlibrary-widget .oae-list.oae-list-grid li + li', function() {
                    // Verify there is an item that was shared with the user
                    casper.test.assertExists('li.oae-list-actions + li', 'The shared item can be found in the second user\'s library');
                });
            });
        });
    });
};

casper.start('http://test.oae.com', function() {
    // Create a couple of users to test with
    var user1 = null;
    var user2 = null;
    userUtil().createUsers(2, function(users) {
        user1 = users[0];
        user2 = users[1];
    });

    // Log in with that user
    casper.then(function() {
        userUtil().doLogIn(user1.username, 'password');
    });

    contentUtil().createFile(null, function(contentURL) {
        casper.thenOpen('http://test.oae.com/me/library');

        // Verify opening the popover with trigger
        casper.then(function() {
            casper.echo('Verify content library share popover', 'INFO');
            verifyOpenLibraryShare();
        });

        // Verify the share form elements are present
        casper.then(function() {
            casper.echo('Verify content library share popover', 'INFO');
            verifyLibraryShareElements();
        });

        // Verify that a content item can be shared with another user
        casper.then(function() {
            casper.echo('Verify content item can be shared', 'INFO');
            verifySharing(user2);
        });
    });

    // Log out at the end of the test
    casper.then(function() {
        userUtil().doLogOut();
    });
});

casper.run(function() {
    this.test.done();
});
